CREATE OR REPLACE PROCEDURE finalizar_recist_medicao ( nr_sequencia_p bigint, ie_opcao_p bigint) AS $body$
BEGIN
	if (ie_opcao_p = 1) then
		begin

		UPDATE RECIST_REGISTRO
		SET DT_FIM = clock_timestamp()
		WHERE nr_sequencia  = nr_sequencia_p;
		end;
		else
			begin
			UPDATE RECIST_REGISTRO
			SET DT_FIM  = NULL
			WHERE nr_sequencia  = nr_sequencia_p;
			end;
	end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
