CREATE OR REPLACE PROCEDURE finalizar_senha_se_pendente ( nr_seq_senha_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_commit_p text default 'S') AS $body$
DECLARE

qt_senha_finalizada_w	bigint;
				

BEGIN 
 
if (coalesce(nr_seq_senha_p,0) > 0) then 
 
	select	count(*) 
	into STRICT	qt_senha_finalizada_w 
	from	paciente_senha_fila 
	where	nr_sequencia = nr_seq_senha_p 
	and	(dt_inicio_atendimento IS NOT NULL AND dt_inicio_atendimento::text <> '') 
	and	coalesce(dt_fim_atendimento::text, '') = '';
	 
	if (coalesce(qt_senha_finalizada_w,0) > 0) then 
	 
		CALL iniciar_finalizar_atend_senha(nr_seq_senha_p,'F',nm_usuario_p,cd_estabelecimento_p,ie_commit_p);	
	end if;
 
end if;
 
if (coalesce(ie_commit_p,'S') = 'S') then 
 
	commit;
end if;
end;
