CREATE OR REPLACE PROCEDURE finalizar_tarefa_tasy ( nr_sequencia_p bigint, nm_usuario_p text ) AS $body$
DECLARE


dt_final_w	timestamp;


BEGIN

begin
select		max(dt_final)
into STRICT		dt_final_w
from		tarefa_tasy
where		nr_sequencia = nr_sequencia_p;
exception
	when no_data_found then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(266279);
end;


if (coalesce(dt_final_w::text, '') = '') then
	update		tarefa_tasy
	set		dt_final		=	clock_timestamp(),
			ie_status		=	'S',
			nm_usuario	=	nm_usuario_p
	where		nr_sequencia = nr_sequencia_p;
else
	update		tarefa_tasy
	set		ie_status		=	'S',
			nm_usuario	=	nm_usuario_p
	where		nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
