SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE finalizar_tratamento_obito (cd_pessoa_fisica_p text, nr_seq_motivo_fim_p bigint, nm_usuario_p text, dt_fim_tratamento_p timestamp) AS $body$
DECLARE

					
ie_tipo_tratamento_w	varchar(15);
					

BEGIN

if (coalesce(nr_seq_motivo_fim_p,0) > 0) then

	select 	max(ie_tipo_tratamento)
	into STRICT	ie_tipo_tratamento_w
	from	motivo_fim
	where	nr_sequencia = nr_seq_motivo_fim_p;
	
	if (ie_tipo_tratamento_w = 'O') then
	
		update 	paciente_tratamento
		set	dt_final_tratamento 	= coalesce(dt_fim_tratamento_p,clock_timestamp()),
			nr_seq_motivo_fim	= nr_seq_motivo_fim_p,
			nm_usuario		= nm_usuario_p
		where 	cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and	coalesce(dt_final_tratamento::text, '') = '';	
		
		update pessoa_fisica
      		set dt_obito = coalesce(dt_fim_tratamento_p,clock_timestamp()),
      		dt_atualizacao = clock_timestamp(),
      		nm_usuario = nm_usuario_p
    		where cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	end if;
	
	
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
