CREATE OR REPLACE PROCEDURE finaliza_evento_cirurgia ( nr_cirurgia_p bigint, dt_evento_p timestamp, nr_seq_pepo_p bigint default 0) AS $body$
DECLARE



nr_seq_equipamento_w	bigint;
dt_evento_w		timestamp;

c01 CURSOR FOR
SELECT	nr_sequencia
from	equipamento_cirurgia
where	((nr_cirurgia = nr_cirurgia_p) or (nr_seq_pepo = nr_seq_pepo_p))
and	coalesce(ie_situacao,'A') = 'A'
and	coalesce(dt_fim::text, '') = '';


BEGIN
dt_evento_w := dt_evento_p;

if (coalesce(dt_evento_w::text, '') = '') and (coalesce(nr_cirurgia_p,0) > 0) then
	dt_evento_w := obter_data_fim_equip(nr_cirurgia_p);
elsif (coalesce(dt_evento_w::text, '') = '') and (coalesce(nr_seq_pepo_p,0) > 0) then
	dt_evento_w := obter_data_fim_equip(null,nr_seq_pepo_p);
end if;


open c01;
loop
fetch c01 into
	nr_seq_equipamento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

	update	equipamento_cirurgia
	set	dt_fim = dt_evento_w
	where	nr_sequencia = nr_seq_equipamento_w;

	end;
end loop;
close c01;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
