CREATE OR REPLACE PROCEDURE finaliza_evento_treinamento ( nr_sequencia_p bigint, nr_seq_motivo_p bigint, dt_encerramento_p timestamp, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_inscrito_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	tre_inscrito
	where	nr_seq_evento = nr_sequencia_p;


BEGIN

	open c01;
	loop
	fetch c01 into
		nr_seq_inscrito_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			update	tre_inscrito
			set	dt_desistencia			= dt_encerramento_p,
				nr_seq_motivo_desistencia 	= nr_seq_motivo_p,
				dt_atualizacao			= clock_timestamp(),
				nm_usuario			= nm_usuario_p,
				ds_observacao			= ds_observacao_p
			where	nr_sequencia			= nr_seq_inscrito_w;
		end;
	end loop;
	close c01;

	update	tre_evento
	set	dt_fim_real = dt_encerramento_p,
		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
