CREATE OR REPLACE PROCEDURE finaliza_gasoterapia_atend ( nr_atendimento_p bigint, nm_usuario_p text, dt_alteracao_p timestamp default clock_timestamp()) AS $body$
DECLARE


nr_seq_w	bigint;
nr_prescricao_w	bigint;
ds_mensagem_w	varchar(255);

c01 CURSOR FOR
	SELECT 	a.nr_sequencia,
			c.nr_prescricao
	from   	PRESCR_GASOTERAPIA a,
			prescr_medica c
	where  	a.nr_prescricao = c.nr_prescricao
	and    	c.nr_atendimento = nr_atendimento_p
	and (Obter_Status_Gasoterapia(a.nr_sequencia, 'C') in ('I','R','V','IN') or (exists (	SELECT 	1
						from 	prescr_gasoterapia_hor b
						where	b.nr_seq_gasoterapia = a.nr_sequencia
						and		obter_status_etapa_gas(b.nr_seq_gasoterapia,b.nr_etapa,b.dt_fim_horario,b.dt_suspensao) = 'I')));



BEGIN

open c01;
loop
fetch c01 into
	nr_seq_w,
	nr_prescricao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_mensagem_w := Gerar_Alteracao_Gasoterapia(nr_seq_w, 'T', 0, '', dt_alteracao_p, nr_prescricao_w, '', 0, nm_usuario_p, '', '', null, null, ds_mensagem_w);
end loop;
close c01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
