CREATE OR REPLACE PROCEDURE fin_alterar_movimento_js ( nr_seq_retorno_p bigint, vl_atual_p text, vl_novo_p text, nm_usuario_p text, ie_alterar_todos_p text) AS $body$
BEGIN

if (ie_alterar_todos_p = 'S') then
	if (coalesce(vl_atual_p::text, '') = '') then
			update	convenio_retorno_movto
			set	ie_tipo_guia	= substr(vl_novo_p,1,2),
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_seq_retorno	= nr_seq_retorno_p
			and	coalesce(ie_tipo_guia::text, '') = '';
		else
			update	convenio_retorno_movto
			set	ie_tipo_guia	= substr(vl_novo_p,1,2),
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_seq_retorno	= nr_seq_retorno_p
			and	ie_tipo_guia 	= vl_atual_p;
	end if;
elsif (ie_alterar_todos_p = 'N') then

	if (coalesce(vl_atual_p::text, '') = '') then
		update	convenio_retorno_movto
		set	ie_tipo_guia	= substr(vl_novo_p,1,2),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_retorno_p
		and	coalesce(ie_tipo_guia::text, '') = '';
	else
		update	convenio_retorno_movto
		set	ie_tipo_guia	= substr(vl_novo_p,1,2),
			nm_usuario	= nm_usuario_p,
			dt_atualizacao	= clock_timestamp()
		where	nr_sequencia	= nr_seq_retorno_p
		and	ie_tipo_guia 	= vl_atual_p;

	end if;
end if;

commit;

end;
$body$
