SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fin_aprovar_rela_desp (nm_usuario_p text, nr_seq_viagem_p bigint) AS $body$
DECLARE


nm_usuario_destino_w	varchar(15);
cd_pessoa_fisica_w	varchar(10);


BEGIN
if (nr_seq_viagem_p IS NOT NULL AND nr_seq_viagem_p::text <> '') then
	begin
	update	via_relat_desp
	set	dt_aprovacao		= clock_timestamp(),
		nm_usuario_aprov	= nm_usuario_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_viagem		= nr_seq_viagem_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

	update	via_viagem
	set 	ie_etapa_viagem	= '5',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where 	nr_sequencia	= nr_seq_viagem_p;

	select	max(a.cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from	via_viagem a
	where	nr_sequencia = nr_seq_viagem_p;

	select	max(a.nm_usuario)
	into STRICT	nm_usuario_destino_w
	from	usuario a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_w;

	CALL gerar_comunic_padrao(	clock_timestamp(),
				'Relatório da GV: '|| nr_seq_viagem_p || ' aprovado',
				'O Relatório de despesa da GV: ' || nr_seq_viagem_p || chr(13) || chr(10) ||
				'Aprovador por: ' || obter_nome_usuario(nm_usuario_p),
				nm_usuario_p,
				'N',
				nm_usuario_destino_w,
				'S',
				null,
				'',
				'',
				'',
				clock_timestamp(),
				'',
				'');
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
