CREATE OR REPLACE PROCEDURE fin_atualizar_rat ( nr_sequencia_p bigint, qt_hora_cobrar_p bigint, vl_hora_cobrar_p bigint) AS $body$
DECLARE


nr_seq_rat_w					bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	proj_rat a
where	a.ie_status_rat <> 'AF'
and	a.nr_seq_proj = nr_sequencia_p;


BEGIN
update	proj_rat
set	vl_hora_cobrar = vl_hora_cobrar_p
where	nr_seq_proj = nr_sequencia_p
and	ie_status_Rat <> 'AF';

open C01;
loop
fetch C01 into
	nr_seq_rat_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	proj_rat
	set	vl_cobrar_cliente = qt_hora_cobrar_p * vl_hora_cobrar_p
	where	nr_sequencia = nr_seq_rat_w;
	end;
end loop;
close C01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
