SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fin_cancelar_gv ( nr_sequencia_p text, nm_usuario_p text) AS $body$
DECLARE


vl_adiantamento_w	double precision;
vl_alimentacao_w	double precision;
ds_usuario_receb_w	varchar(2000);
nm_usuario_receb_w	varchar(15);
ds_mensagem_fin_w	varchar(4000);

c01 CURSOR FOR
SELECT	nm_usuario_receb
from	fin_regra_receb_ci_gv;


BEGIN
select	coalesce(max(a.vl_adiantamento),0),
	coalesce(max(a.vl_alimentacao),0)
into STRICT	vl_adiantamento_w,
	vl_alimentacao_w
from	fin_gv_pend_aprov a
where	a.nr_seq_viagem = nr_sequencia_p;

if	((vl_adiantamento_w > 0) or (vl_alimentacao_w > 0)) then
	begin
	ds_usuario_receb_w := '';
	open C01;
	loop
	fetch C01 into
		nm_usuario_receb_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_usuario_receb_w :=  ds_usuario_receb_w || nm_usuario_receb_w || ',';
		end;
	end loop;
	ds_usuario_receb_w := ds_usuario_receb_w || nm_usuario_p;
	close C01;
	ds_mensagem_fin_w := 'GV: ' || nr_sequencia_p;
	if (vl_adiantamento_w > 0) then
		ds_mensagem_fin_w := ds_mensagem_fin_w || chr(13) || chr(10) || 'Possuí R$ '||vl_adiantamento_w|| ' de adiantamento.';
	end if;
	if (vl_alimentacao_w > 0) then
		ds_mensagem_fin_w := ds_mensagem_fin_w || chr(13) || chr(10) || 'Possuí R$ '||vl_alimentacao_w|| ' de alimentação.';
	end if;
	CALL gerar_comunic_padrao(	clock_timestamp(),
				'Estorno de aprovação da GV: '|| nr_sequencia_p,
				ds_mensagem_fin_w,
				nm_usuario_p, 'N', ds_usuario_receb_w, 'S', null, '', '', '', clock_timestamp(), '', '');
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
