CREATE OR REPLACE PROCEDURE fin_cancelar_perda ( nr_seq_perda_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_saldo_w		double precision;
nr_seq_baixa_w		integer;
ie_tipo_perda_w		varchar(1);
nr_seq_tipo_baixa_w	bigint;
dt_perda_w		timestamp;


BEGIN
select	ie_tipo_perda,
	dt_perda
into STRICT	ie_tipo_perda_w,
	dt_perda_w
from	perda_contas_receber
where	nr_sequencia = nr_seq_perda_p;

select	max(nr_sequencia)
into STRICT	nr_seq_tipo_baixa_w
from	fin_tipo_baixa_perda
where	ie_tipo_consistencia = '4'
and	ie_situacao = 'A';

if (coalesce(nr_seq_tipo_baixa_w::text, '') = '') then
	begin
	select	nextval('fin_tipo_baixa_perda_seq')
	into STRICT	nr_seq_tipo_baixa_w
	;

	insert into fin_tipo_baixa_perda(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_tipo_baixa,
		ie_tipo_consistencia,
		ie_situacao)
	values (	nr_seq_tipo_baixa_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		wheb_mensagem_pck.get_texto(303198), -- Cancelamento
		'4',
		'A');
	end;
end if;

if (ie_tipo_perda_w = 'I') then
	begin
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_baixa_w
	from	perda_contas_receb_baixa a,
		fin_tipo_baixa_perda b
	where	a.nr_seq_perda = nr_seq_perda_p
	and	a.nr_seq_tipo_baixa = b.nr_sequencia
	and	b.ie_tipo_consistencia = '2'
	and	a.ie_acao = '1';

	if (nr_seq_baixa_w IS NOT NULL AND nr_seq_baixa_w::text <> '') then
		CALL fin_estornar_baixa_perda(nr_seq_perda_p, nr_seq_baixa_w, null, null, nm_usuario_p);
	end if;
	end;
end if;

select	vl_saldo
into STRICT	vl_saldo_w
from	perda_contas_receber
where	nr_sequencia = nr_seq_perda_p;

CALL fin_gerar_baixa_perda(nr_seq_perda_p, vl_saldo_w, nr_seq_tipo_baixa_w, '1', null, dt_perda_w, nm_usuario_p);

CALL fin_atualiza_cobranca_perda(nr_Seq_perda_p, 'CP', 'N', nm_usuario_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
