SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fin_duplicar_regra ( nr_sequencia_p bigint, nm_usuario_p text, pr_reajuste_p bigint) AS $body$
DECLARE


nr_seq_regra_w			bigint;
nr_seq_regra_item_w		bigint;
nr_seq_regra_item_ww		bigint;
nr_seq_regra_resp_w		bigint;
nr_seq_regra_resp_ww		bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	com_cli_regra_valor_item
	where	nr_seq_regra = nr_sequencia_p
	order by nr_sequencia;

c02 CURSOR FOR
	SELECT	nr_sequencia
	from	com_cli_regra_valor_rep
	where	nr_seq_regra_item = nr_seq_regra_item_ww
	order by nr_sequencia;


BEGIN

select	nextval('com_cliente_regra_valor_seq')
into STRICT	nr_seq_regra_w
;

insert into com_cliente_regra_valor(
	nr_sequencia,
	cd_estabelecimento,
	nr_seq_cliente,
	dt_atualizacao,
	nm_usuario,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	ds_observacao,
	cd_operacao_nf,
	cd_condicao_pagamento,
	ie_atualizar,
	ie_situacao,
	cd_natureza_operacao,
	cd_cnpj_pessoa_nf)
SELECT	nr_seq_regra_w,
	cd_estabelecimento,
	nr_seq_cliente,
	clock_timestamp(),
	nm_usuario_p,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	wheb_mensagem_pck.get_texto(300346, 'DS_OBSERVACAO=' || ds_observacao),
	cd_operacao_nf,
	cd_condicao_pagamento,
	ie_atualizar,
	ie_situacao,
	cd_natureza_operacao,
	cd_cnpj_pessoa_nf
from	com_cliente_regra_valor
where	nr_sequencia = nr_sequencia_p;

open c01;
loop
fetch c01 into
	nr_seq_regra_item_ww;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('com_cli_regra_valor_item_seq')
	into STRICT	nr_seq_regra_item_w
	;

	insert into com_cli_regra_valor_item(
		nr_sequencia,
		nr_seq_regra,
		dt_atualizacao,
		nm_usuario,
		ds_titulo,
		vl_cobrar,
		cd_procedimento,
		ie_origem_proced,
		cd_conta_contabil,
		cd_centro_custo,
		ie_rat,
		ds_compl_nf,
		ie_situacao)
	SELECT	nr_seq_regra_item_w,
		nr_seq_regra_w,
		clock_timestamp(),
		nm_usuario_p,
		ds_titulo,
		CASE WHEN pr_reajuste_p=0 THEN vl_cobrar  ELSE (vl_cobrar * (1 + pr_reajuste_p/100)) END ,
		cd_procedimento,
		ie_origem_proced,
		cd_conta_contabil,
		cd_centro_custo,
		ie_rat,
		ds_compl_nf,
		ie_situacao
	from	com_cli_regra_valor_item
	where	nr_sequencia = nr_seq_regra_item_ww;

	open c02;
	loop
	fetch c02 into
		nr_seq_regra_resp_ww;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		select	nextval('com_cli_regra_valor_rep_seq')
		into STRICT	nr_seq_regra_resp_w
		;

		insert into com_cli_regra_valor_rep(
			nr_sequencia,
			nr_seq_regra_item,
			dt_atualizacao,
			nm_usuario,
			ds_titulo,
			vl_repasse,
			pr_repasse,
			cd_pessoa_fisica,
			cd_cnpj,
			ie_situacao,
			cd_material,
			cd_cond_pagto,
			dt_vigencia)
		SELECT	nr_seq_regra_resp_w,
			nr_seq_regra_item_w,
			clock_timestamp(),
			nm_usuario_p,
			ds_titulo,
			CASE WHEN pr_reajuste_p=0 THEN vl_repasse  ELSE (vl_repasse * (1 + pr_reajuste_p/100)) END ,
			pr_repasse,
			cd_pessoa_fisica,
			cd_cnpj,
			ie_situacao,
			cd_material,
			cd_cond_pagto,
			dt_vigencia
		from	com_cli_regra_valor_rep
		where	nr_sequencia = nr_seq_regra_resp_ww;
		end;
	end loop;
	close c02;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fin_duplicar_regra ( nr_sequencia_p bigint, nm_usuario_p text, pr_reajuste_p bigint) FROM PUBLIC;

