CREATE OR REPLACE PROCEDURE fin_excluir_nf ( nr_seq_cliente_p bigint, nr_seq_nf_p bigint, nm_usuario_p text) AS $body$
DECLARE



qt_reg_w		bigint;
qt_atual_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	nota_fiscal
where	nr_sequencia		= nr_seq_nf_p;
if (qt_reg_w = 0) then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279130);
end if;

select	count(*)
into STRICT	qt_reg_w
from	nota_fiscal
where	nr_sequencia		= nr_seq_nf_p
and	(dt_atualizacao_estoque IS NOT NULL AND dt_atualizacao_estoque::text <> '');
if (qt_reg_w > 0) then
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279131);
end if;

update	Proj_Rat
set	nr_seq_nf		 = NULL
where	nr_seq_nf		= nr_seq_nf_p;


delete	from	com_cliente_nf b
where	b.nr_seq_cliente	= nr_seq_cliente_p
and	b.nr_seq_nf		= nr_seq_nf_p;

delete from ordem_compra
where	nr_ordem_compra in (SELECT nr_ordem_compra
	from	nota_fiscal_item_rep
	where	nr_seq_nf	= nr_seq_nf_p);

select	count(*)
into STRICT	qt_reg_w
from	nota_fiscal_item
where	nr_sequencia	= nr_seq_nf_p;

if (qt_reg_w > 0) then
	delete from nota_fiscal_item
	where	nr_Sequencia = nr_seq_nf_p;
end if;

delete	from nota_fiscal
where	nr_sequencia		= nr_seq_nf_p;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
