CREATE OR REPLACE PROCEDURE fin_gerar_baixa_perda ( nr_seq_perda_p bigint, vl_baixa_p bigint, nr_seq_tipo_baixa_p bigint, ie_acao_p text, nr_seq_movto_trans_p bigint, dt_baixa_p timestamp, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		integer;


BEGIN
select	coalesce(max(nr_sequencia),0) + 1
into STRICT	nr_sequencia_w
from	perda_contas_receb_baixa
where	nr_seq_perda = nr_seq_perda_p;

insert into perda_contas_receb_baixa(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	nr_seq_perda,
	dt_baixa,
	vl_baixa,
	nr_seq_tipo_baixa,
	ie_acao,
	nr_seq_movto_trans_fin)
values (	nr_sequencia_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_perda_p,
	dt_baixa_p,
	vl_baixa_p,
	nr_seq_tipo_baixa_p,
	ie_acao_p,
	nr_seq_movto_trans_p);

CALL fin_atualizar_saldo_perda(nr_seq_perda_p, nm_usuario_p);

end;
$body$
