CREATE OR REPLACE PROCEDURE fin_gerar_faturamento (cd_estabelecimento_p bigint, dt_referencia_p timestamp, ie_rat_p text, ie_regra_p text, ie_proc_espec_p text, nr_seq_cliente_p bigint, cd_setor_atendimento_p bigint, nm_usuario_p text, ie_calcular_nf_p text default 'N', ie_faturar_restricao_p text default 'N', ie_faturar_rat_projeto_p text default 'N') AS $body$
DECLARE

 
nr_seq_cliente_w			bigint;
ie_restricao_w				Com_Cliente.ie_restricao%type;			
 
C01 CURSOR FOR 
SELECT	distinct(nr_seq_cliente) 
from	Com_Cliente_Regra_Valor 
where	cd_estabelecimento			= cd_estabelecimento_p 
and	coalesce(nr_seq_cliente_p, nr_seq_cliente)	= nr_seq_cliente;


BEGIN 
 
open C01;
LOOP 
FETCH C01 into 
	nr_seq_cliente_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	 
	select	max(ie_restricao) 
	into STRICT	ie_restricao_w 
	from	com_cliente 
	where 	nr_sequencia = nr_seq_cliente_w;
		 
	if	((coalesce(ie_restricao_w,'N') <> 'S') or (coalesce(ie_faturar_restricao_p,'N') = 'S'))then 
		 
		CALL Fin_Gerar_fatur_Cliente(	cd_estabelecimento_p, 
						dt_referencia_p, 
						nr_seq_cliente_w, 
						ie_rat_p, 
						ie_regra_p, 
						ie_proc_espec_p, 
						cd_setor_atendimento_p, 
						nm_usuario_p, 
						ie_calcular_nf_p, 
						ie_faturar_rat_projeto_p);
	end if;
	end;
END LOOP;
CLOSE C01;
 
END;
$body$
