SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fin_gerar_perc_tit_cob ( nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tit_cob_w			bigint;
pr_indice_w			double precision;
vl_saldo_tit_w			double precision;
vl_saldo_lote_w			double precision;

c01 CURSOR FOR
SELECT	distinct
	a.nr_seq_cliente
from	fin_titulo_lote_clie a
where	a.nr_lote_cliente = nr_seq_lote_p
and	(a.nr_lote_ref IS NOT NULL AND a.nr_lote_ref::text <> '');


BEGIN
select	sum(coalesce(a.vl_titulo,0))
into STRICT	vl_saldo_lote_w
from	fin_titulo_lote_clie a
where	a.nr_lote_cliente = nr_seq_lote_p
and	(a.nr_lote_ref IS NOT NULL AND a.nr_lote_ref::text <> '');

open C01;
loop
fetch C01 into
	nr_seq_tit_cob_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	sum(coalesce(a.vl_titulo,0))
	into STRICT	vl_saldo_tit_w
	from	fin_titulo_lote_clie a
	where	a.nr_seq_cliente = nr_seq_tit_cob_w
	and	a.nr_lote_cliente = nr_seq_lote_p
	and	(a.nr_lote_ref IS NOT NULL AND a.nr_lote_ref::text <> '');

	if (coalesce(vl_saldo_lote_w,0) > 0) then
		pr_indice_w := ((vl_saldo_tit_w * 100) / vl_saldo_lote_w);
	else	pr_indice_w := 0;
	end if;

	update	fin_titulo_lote_clie
	set	pr_indice = pr_indice_w
	where	nr_seq_cliente = nr_seq_tit_cob_w
	and	nr_lote_cliente = nr_seq_lote_p;
	end;
end loop;
close C01;

update	fin_lote_cobranca
set	vl_saldo = vl_saldo_lote_w
where	nr_sequencia = nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
