CREATE OR REPLACE PROCEDURE fin_gravar_log_regra_repasse ( nm_tabela_p text, ie_log_p text, nr_seq_tab_orig_p bigint, ds_historico_p text, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w				text;
nr_sequencia_w				bigint;

/* Caso Exista Formatação Negrito */

qt_existe_Format_w			bigint;
qt_existe_w				bigint;


BEGIN

ds_historico_w	:= substr(ds_historico_p,1,4000);

select	position('{\ul{\b' in ds_historico_w)
into STRICT	qt_existe_Format_w
;

if (qt_existe_Format_w > 0) then
	ds_historico_w	:= substr('{\rtf1\ansi\deff0{\fonttbl ' ||
				'{\f0\fnil\fcharset0 Verdana;}{\f1\fnil Verdana;}}' ||
				'\viewkind4\uc1\pard\cf1\lang1046\f0\fs20' ||
				ds_historico_w || '\b0 }',1,4000);
end if;


insert into regra_repasse_log(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nm_tabela,
	ie_log,
	nr_seq_tab_orig,
	ds_historico_curto,
	ds_historico)
values (	nextval('regra_repasse_log_seq'),
	clock_timestamp(),
	nm_usuario_p,
	nm_tabela_p,
	ie_log_p,
	nr_seq_tab_orig_p,
	substr(ds_historico_w,1,4000),
	ds_historico_w);

commit;

end;
