CREATE OR REPLACE PROCEDURE fin_historico_regra_cobr ( nm_usuario_p text, nr_seq_historico_p bigint, nr_seq_cobranca_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_regra_w		regra_hist_reg_cobr.nr_sequencia%type;
nr_seq_hist_reg_w		tipo_hist_cob.nr_sequencia%type;
qt_dias_prev_pagto_w	regra_hist_reg_cobr.qt_dias_prev_pagto%type;
dt_prev_pagto_w		timestamp;
nr_seq_registro_w		registro_cobranca.nr_sequencia%type;
qt_registro_w		integer;

C01 CURSOR FOR
SELECT	nr_seq_hist_reg,
	coalesce(qt_dias_prev_pagto,0)
from	regra_hist_reg_cobr
where	nr_seq_historico = nr_seq_historico_p
and	coalesce(cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	regra_hist_reg_cobr
where	nr_seq_historico = nr_seq_historico_p;

if (qt_registro_w > 0)	then

	open C01;
	loop
	fetch C01 into
		nr_seq_hist_reg_w,
		qt_dias_prev_pagto_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		select	nextval('registro_cobranca_seq')
		into STRICT	nr_seq_registro_w
		;
		dt_prev_pagto_w := clock_timestamp() + qt_dias_prev_pagto_w;

		insert	into	registro_cobranca(	nr_sequencia,
				dt_registro,
				dt_prev_pagto,
				nr_seq_historico,
				nm_usuario,
				nm_usuario_nrec,
				dt_atualizacao,
				dt_atualizacao_nrec,
				cd_estabelecimento,
				ie_proprio_devedor)
			values (	nr_seq_registro_w,
				clock_timestamp(),
				dt_prev_pagto_w,
				nr_seq_hist_reg_w,
				nm_usuario_p,
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp(),
				cd_estabelecimento_p,
				'N');

		insert 	into 	registro_cobr_item(	nr_sequencia,
				nm_usuario,
				dt_atualizacao,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				nr_seq_registro,
				nr_seq_cobranca)
			values (	nextval('registro_cobr_item_seq'),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nr_seq_registro_w,
				nr_seq_cobranca_p);

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
