SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fin_importar_conv_ret_movto ( nr_seq_retorno_p bigint, nm_usuario_p text) AS $body$
DECLARE



c01 CURSOR FOR
SELECT	nr_sequencia
from	convenio_retorno_movto
where	coalesce(vl_pago::text, '') = ''
and	vl_total_pago > 0
and	qt_paga > 0
and	nr_seq_retorno	= nr_seq_retorno_p;

vet01	c01%rowtype;

qt_registros_w	bigint	:= 0;


BEGIN

open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	qt_registros_w	:= qt_registros_w + 1;

	update	convenio_retorno_movto
	set	vl_pago = coalesce(dividir_sem_round(coalesce(vl_total_pago, 0), coalesce(qt_paga, 0)), 0)
	where	coalesce(vl_pago::text, '') = ''
	and	vl_total_pago > 0
	and	qt_paga > 0
	and	nr_seq_retorno = vet01.nr_sequencia;

	if (qt_registros_w >= 2000) then
		begin
		qt_registros_w	:= 0;
		commit;
		end;
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
