CREATE OR REPLACE PROCEDURE fin_liberar_estornar_retorno ( nr_seq_retorno_p bigint, ie_opcao_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (coalesce(ie_opcao_p, -1) = 0) then /*Liberar*/
	update	convenio_retorno
	set	dt_liberacao	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_retorno_p;

elsif (coalesce(ie_opcao_p, -1) = 1) then /*Estornar*/
	update   convenio_retorno
	set	dt_liberacao	 = NULL,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
         	where	nr_sequencia	= nr_seq_retorno_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
