SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fin_obter_se_mes_ctb_fechado ( cd_estab_p integer, dt_referencia_p timestamp) AS $body$
DECLARE


nr_sequencia_w		ctb_mes_ref.nr_sequencia%type;
ie_fechado_w		varchar(1);


BEGIN

select	max(coalesce(nr_sequencia,0))
into STRICT	nr_sequencia_w
from	ctb_mes_ref r
where	r.cd_empresa = obter_empresa_estab(cd_estab_p)
and	r.dt_referencia = trunc(dt_referencia_p,'mm');

if (nr_sequencia_w > 0) then
	select	ctb_obter_se_mes_fechado(nr_sequencia_w, cd_estab_p)
	into STRICT	ie_fechado_w
	;

	if (ie_fechado_w = 'F') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(296872);
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
