CREATE OR REPLACE PROCEDURE fis_ajustar_nota_reenvio ( dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text) AS $body$
BEGIN

update 	nfe_transmissao
set 	ie_status_transmissao 	 = NULL,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where 	dt_atualizacao 		between dt_inicio_p and fim_dia(dt_fim_p) 
and     	coalesce(obter_protocolo_trans(nr_sequencia)::text, '') = ''
and     	ie_status_transmissao 	<> 'T';

update 	nota_fiscal
set 	ie_status_envio 	 = NULL,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where 	ie_status_envio 	in ('N','T')  
and 	dt_emissao 	between dt_inicio_p and fim_dia(dt_fim_p)
and     	ie_status_envio 	<>  'E'
and	coalesce(nr_nfe_imp::text, '') = '';


/*delete 	nfe_transmissao_nf a
where	dt_atualizacao 	between dt_inicio_p and fim_dia(dt_fim_p) 
and     	not exists (	select 1
			from  nfe_transmissao b
			where b.nr_sequencia = a.nr_seq_transmissao
			and   obter_protocolo_trans(b.nr_sequencia) is not null
			and   b.ie_status_transmissao = 'T');*/
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
