SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_atualizar_log_fiscal () AS $body$
DECLARE

 
cd_material_w		bigint;
nr_nota_fiscal_w		bigint;
nr_sequencia_w		bigint;
qt_registro_w		bigint;

c01 CURSOR FOR 
	SELECT	cd_material, 
		nr_seq_nota_fiscal, 
		nr_sequencia 
	from	fis_variacao_fiscal_log 
	where	coalesce(ie_status::text, '') = '';


BEGIN 
 
	open c01;
	loop 
	fetch c01 into cd_material_w, 
		    nr_nota_fiscal_w, 
		    nr_sequencia_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
			if (nr_nota_fiscal_w > 0 ) then 
			qt_registro_w	:= fis_consistir_variacao_fiscal(cd_material_w, nr_nota_fiscal_w);
			end if;
			if (qt_registro_w > 0) then 
				update	fis_variacao_fiscal_log 
				set	ie_status = 'C' 
				where	nr_sequencia = nr_sequencia_w;
			end if;
		 
	end loop;
	close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
