CREATE OR REPLACE PROCEDURE fis_carregar_estrutura_livros (ie_lalur_lacs_p text, ie_tipo_estrutura_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_estrutura_w		varchar(255);
nr_seq_ordem_w		bigint;
ie_lalur_lacs_w		varchar(2);
ie_tipo_estrutura_w	varchar(2);
ie_forma_apuracao_w	varchar(2);
ie_gerar_anual_w	varchar(2);

c01 CURSOR FOR
SELECT	nr_sequencia,
	ds_estrutura,
	nr_seq_ordem,
	ie_lalur_lacs,
	ie_tipo_estrutura,
	ie_forma_apuracao,
	CASE WHEN ie_tipo_estrutura='DE' THEN  CASE WHEN ie_forma_apuracao='R' THEN  'S'  ELSE 'N' END   ELSE 'N' END
from	fis_livros_estr_padrao p
where	not exists (select 1
		from fis_estrutura_livros
		where nr_seq_estrutura = p.nr_sequencia
		and cd_empresa = obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento)  LIMIT 1)
and	ie_lalur_lacs = ie_lalur_lacs_p
and	ie_tipo_estrutura = ie_tipo_estrutura_p
and	ie_situacao = 'A'
order by nr_seq_ordem;


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	ds_estrutura_w,
	nr_seq_ordem_w,
	ie_lalur_lacs_w,
	ie_tipo_estrutura_w,
	ie_forma_apuracao_w,
	ie_gerar_anual_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into fis_estrutura_livros(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		cd_conta_contabil,
		vl_fixo,
		ie_gerar_estrutura,
		nr_seq_estrutura,
		ds_estrutura,
		nr_seq_ordem,
		ie_lalur_lacs,
		ie_tipo_estrutura,
		ie_forma_apuracao,
		ie_gerar_anual,
		cd_empresa)
	values (
		nextval('fis_estrutura_livros_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		null,
		null,
		'S',
		nr_sequencia_w,
		ds_estrutura_w,
		nr_seq_ordem_w,
		ie_lalur_lacs_w,
		ie_tipo_estrutura_w,
		ie_forma_apuracao_w,
		ie_gerar_anual_w,
		obter_empresa_estab(wheb_usuario_pck.get_cd_estabelecimento));
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
