CREATE OR REPLACE PROCEDURE fis_consistir_itens_log ( nr_seq_nota_p bigint) AS $body$
DECLARE


cd_material_w	bigint;
calcula_log_w	integer;

C01 CURSOR FOR
	SELECT	l.cd_material
	from	fis_variacao_fiscal_log l
	where	l.nr_seq_nota_fiscal      = nr_seq_nota_p;


BEGIN

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(nr_item_nf)
	into STRICT	calcula_log_w
	from	nota_fiscal_item
	where	cd_material	= cd_material_w
	and	nr_sequencia	= nr_seq_nota_p;

	if (calcula_log_w = 0)  then

		delete	from fis_variacao_fiscal_log
		where	cd_material = cd_material_w
		and	nr_seq_nota_fiscal	= nr_seq_nota_p;

	end if;

	end;
end loop;
close C01;




commit;

end;
