SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_desfazer_reinf_4010 (nr_seq_superior_p bigint, nm_usuario_p text) AS $body$
DECLARE

				
qtd_w bigint;

BEGIN

    select count(*)
    into STRICT   qtd_w
    from   fis_reinf_r4010_evento
    where  nr_seq_lote = nr_seq_superior_p
    and    (dt_transmissao IS NOT NULL AND dt_transmissao::text <> '');

    if (qtd_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1080708);
    end if;


    delete from fis_reinf_r4010_evento 
    where  nr_seq_lote = nr_seq_superior_p;

    update fis_reinf_r4010 
    set    dt_geracao  = NULL 
    where  nr_sequencia = nr_seq_superior_p;
			
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
