CREATE OR REPLACE PROCEDURE fis_duplicar_variacao_fiscal (nr_seq_variacao_ant_p text, dt_inicio_p timestamp, dt_fim_p timestamp, nm_usuario_p text) AS $body$
DECLARE

ie_situacao_w	varchar(2);

BEGIN
select 	coalesce(ie_situacao,'A')
into STRICT	ie_situacao_w
from	fis_variacao_fiscal
where	nr_sequencia = nr_seq_variacao_ant_p;

if (ie_situacao_w = 'I') then
	insert into fis_variacao_fiscal(nr_sequencia,
					 dt_atualizacao,
					 nm_usuario,
					 dt_atualizacao_nrec,
					 nm_usuario_nrec,
					 cd_grupo_material,
					 dt_inicio_vigencia,
					 dt_fim_vigencia,
					 cd_pessoa_fisica,
					 cd_cnpj,
					 sg_estado,
					 cd_subgrupo_material,
					 cd_classe_material,
					 cd_material,
					 cd_tipo_material,
					 cd_operacao_nf,
					 cd_natureza_operacao,
					 ie_origem_mercadoria,
					 ie_tributacao_icms,
					 nr_base_calculo_icms,
					 nr_aliquota_icms,
					 nr_reducao_base_calculo_icms,
					 cd_codigo_valores_fiscais,
					 cd_dispositivo_legal,
					 ie_situacao)
				(SELECT nextval('fis_variacao_fiscal_seq'),
					 clock_timestamp(),
					 nm_usuario_p,
					 clock_timestamp(),
					 nm_usuario_p,
					 cd_grupo_material,
					 dt_inicio_p,
					 dt_fim_p,
					 cd_pessoa_fisica,
					 cd_cnpj,
					 sg_estado,
					 cd_subgrupo_material,
					 cd_classe_material,
					 cd_material,
					 cd_tipo_material,
					 cd_operacao_nf,
					 cd_natureza_operacao,
					 ie_origem_mercadoria,
					 ie_tributacao_icms,
					 nr_base_calculo_icms,
					 nr_aliquota_icms,
					 nr_reducao_base_calculo_icms,
					 cd_codigo_valores_fiscais,
					 cd_dispositivo_legal,
					 'A'
				from	fis_variacao_fiscal
				where	nr_sequencia = nr_seq_variacao_ant_p);
else
	begin
	CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(279323);
	end;
end if;

commit;

end;
$body$
