CREATE OR REPLACE PROCEDURE fis_efd_desmarcar_reg ( nr_seq_regra_efd_p bigint, nr_seq_superior_p bigint, qt_contador_p INOUT bigint) AS $body$
DECLARE



cd_registro_w		fis_efd_cad_registro.cd_registro%type;
nr_sequencia_w		fis_efd_cad_registro.nr_sequencia%type;
qt_contador_w		bigint := qt_contador_p;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		cd_registro
	from	fis_efd_cad_registro
	where	nr_seq_reg_superior = nr_seq_superior_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	cd_registro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_contador_w := coalesce(qt_contador_w,0) + 1;

	update	fis_regra_efd_reg a
	set	a.ie_gerar 	= 'N'
	where 	a.cd_registro 	= cd_registro_w
	and	nr_seq_regra_efd = nr_seq_regra_efd_p;

	if (qt_contador_w < 50) then
		qt_contador_p := qt_contador_w;
		qt_contador_w := fis_efd_desmarcar_reg(nr_seq_regra_efd_p, nr_sequencia_w, qt_contador_w);
	end if;

	end;
end loop;
close C01;

commit;

qt_contador_p := qt_contador_w;

end;
$body$
