CREATE OR REPLACE PROCEDURE fis_efd_excluir_regra ( nm_usuario_p text, nr_sequencia_p bigint) AS $body$
BEGIN

begin -- Tratamento de exceção
	delete from fis_efd_nota_arq where nr_seq_controle in (		SELECT	nr_sequencia
									from	fis_efd_controle
									where	nr_seq_regra_efd = nr_sequencia_p);

	delete from fis_efd_arquivo where nr_seq_controle_efd in (	SELECT	nr_sequencia
									from	fis_efd_controle
									where	nr_seq_regra_efd = nr_sequencia_p);

	delete from fis_efd_controle where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_a100 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_c100 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_c500 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_d100 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_d500 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_f600 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_m where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_m210 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_m220 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_m400 where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd_reg where nr_seq_regra_efd = nr_sequencia_p;
	delete from fis_regra_efd where nr_sequencia = nr_sequencia_p;

exception
when others then
	rollback;
	CALL wheb_mensagem_pck.exibir_mensagem_abort(326208,'ds_erro=' || substr(SQLERRM,1,255) );
end;

commit;

end;
$body$
