SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_envio_rps_sequencial ( cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_transmissao_w	nfe_transmissao.nr_sequencia%type;
nr_sequencia_w		nota_fiscal.nr_sequencia%type;
ie_inconsistencia_w	varchar(1);
ie_nfe_transmissao_w	bigint;

C01 CURSOR FOR 
SELECT 	n.nr_sequencia 
from 	nota_fiscal n, 
	operacao_nota o 
where 	n.cd_operacao_nf = o.cd_operacao_nf 
and	((n.dt_atualizacao_estoque IS NOT NULL AND n.dt_atualizacao_estoque::text <> '' AND n.ie_situacao = 1) or ((coalesce(n.dt_atualizacao_estoque::text, '') = '') and (n.ie_situacao = 8))) 
and	o.ie_servico = 'S' 
and	o.ie_nf_eletronica = 'S' 
and	o.ie_operacao_fiscal = 'S' 
and	coalesce(n.nr_nfe_imp::text, '') = '' 
and	coalesce(n.ie_status_envio::text, '') = '' 
and	n.cd_estabelecimento = cd_estabelecimento_p 
and (SELECT coalesce(max(1),0) 
	from	nfe_transmissao a, 
		nfe_transmissao_nf b 
	where	a.nr_sequencia = b.nr_seq_transmissao 
	and	b.nr_seq_nota_fiscal = n.nr_sequencia 
	and	a.ie_tipo_nota = 'NFSE') = 0;


BEGIN 
 
delete	FROM w_nfse_envio_automatico 
where	nm_usuario 	= nm_usuario_p 
and	cd_estabelecimento	= cd_estabelecimento_p;
	 
open C01;
loop 
fetch C01 into	 
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
 
	select 	coalesce(substr(obter_inconsist_nfse_regra(nr_sequencia_w,'2'),1,1),'N') 
	into STRICT	ie_inconsistencia_w 
	;
	 
	if (ie_inconsistencia_w = 'N') then 
 
		nr_seq_transmissao_w := gerar_transmissao_nf(	nm_usuario_p, 1, 'NFSE', nr_seq_transmissao_w);
						 
		if (nr_seq_transmissao_w IS NOT NULL AND nr_seq_transmissao_w::text <> '') then 
			begin 
				 
				CALL gravar_nf_transmissao(nr_seq_transmissao_w, nr_sequencia_w, nm_usuario_p);
				 
				CALL nfe_inserir_lote(nm_usuario_p, 
						 cd_estabelecimento_p, 
						 nr_seq_transmissao_w);
				 
				insert into w_nfse_envio_automatico(	 
					nr_seq_transmissao, 
					cd_estabelecimento, 
					qt_espera_consulta, 
					nm_usuario) 
				values (	nr_seq_transmissao_w, 
					cd_estabelecimento_p, 
					0, 
					nm_usuario_p);
					 
				update	nota_fiscal 
				set	ie_status_envio = 'T' 
				where	nr_sequencia = nr_sequencia_w;
					 
				exit;
			 
			end;
		end if;
	end if;
	 
end loop;
close C01;	
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
