SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_arq_9900_icmsipi ( nr_seq_controle_p bigint, nr_linha_p INOUT bigint ) AS $body$
DECLARE


-- VARIABLES
ds_linha_w			varchar(8000);
ds_sep_w			varchar(1) := '|';

-- USUARIO
nm_usuario_w		usuario.nm_usuario%type;

-- FIS_EFD_ICMSIPI_ARQUIVO
nr_linha_w			fis_efd_icmsipi_arquivo.nr_linha%type;
ds_arquivo_w		fis_efd_icmsipi_arquivo.ds_arquivo%type;
ds_arquivo_compl_w	fis_efd_icmsipi_arquivo.ds_arquivo_compl%type;

c01 CURSOR FOR
	SELECT min(b.nr_linha) ordem,
		   b.cd_registro cd_reg,
		   count(*) qt_linha
	from (	SELECT	a.nr_linha, a.cd_registro
			from	fis_efd_icmsipi_arquivo a
			where	a.nr_seq_controle	= nr_seq_controle_p
			and		substr(a.cd_registro,1,1) <> '9'
			order by a.nr_linha
		 ) b
	group by b.cd_registro
	order by 1;

c02 CURSOR FOR
	SELECT	1 ordem,
			'9001' cd_reg,
			1 qt_linha
	
	
union

	SELECT 	2,
			'9900' cd_reg,
			count(*) + 4 qt_linha
	from 	fis_efd_icmsipi_arquivo a
	where	a.nr_seq_controle	= nr_seq_controle_p
	and		a.cd_registro = '9900'
	
union

	select	3 ordem,
			'9990' cd_reg,
			1 qt_linha
	
	
union

	select	4 ordem,
			'9999' cd_reg,
			1 qt_linha
	;
BEGIN
nm_usuario_w := Obter_Usuario_Ativo;
nr_linha_w := nr_linha_p;

for r01 in c01 loop
	nr_linha_w := nr_linha_w + 1;
	ds_linha_w := substr(ds_sep_w || '9900' ||
						ds_sep_w || r01.cd_reg ||
						ds_sep_w || r01.qt_linha || ds_sep_w,1,8000);
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);

	insert into fis_efd_icmsipi_arquivo(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_controle,
		nr_linha,
		ds_arquivo,
		ds_arquivo_compl,
		cd_registro)
	values (
		nextval('fis_efd_icmsipi_arquivo_seq'),
		clock_timestamp(),
		nm_usuario_w,
		clock_timestamp(),
		nm_usuario_w,
		nr_seq_controle_p,
		nr_linha_w,
		ds_arquivo_w,
		ds_arquivo_compl_w,
		'9900');
end loop;

for r02 in c02 loop
	nr_linha_w := nr_linha_w + 1;
	ds_linha_w := substr(ds_sep_w || '9900' ||
						ds_sep_w || r02.cd_reg ||
						ds_sep_w || r02.qt_linha || ds_sep_w,1,8000);
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);

	insert into fis_efd_icmsipi_arquivo(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_controle,
		nr_linha,
		ds_arquivo,
		ds_arquivo_compl,
		cd_registro)
	values (
		nextval('fis_efd_icmsipi_arquivo_seq'),
		clock_timestamp(),
		nm_usuario_w,
		clock_timestamp(),
		nm_usuario_w,
		nr_seq_controle_p,
		nr_linha_w,
		ds_arquivo_w,
		ds_arquivo_compl_w,
		'9900');
end loop;

nr_linha_p := nr_linha_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
