CREATE OR REPLACE PROCEDURE fis_gerar_arq_encerram_icmsipi ( nr_seq_controle_p bigint, cd_registro_p text, nr_linha_p INOUT bigint ) AS $body$
DECLARE


-- VARIABLES
ds_linha_w			varchar(8000);
ie_gerou_w			bigint;
qt_bloco_w			bigint;
ds_sep_w			varchar(1) := '|';

-- USUARIO
nm_usuario_w		usuario.nm_usuario%type;

-- FIS_EFD_ICMSIPI_ARQUIVO
nr_linha_w			fis_efd_icmsipi_arquivo.nr_linha%type := 0;
ds_arquivo_w		fis_efd_icmsipi_arquivo.ds_arquivo%type;
ds_arquivo_compl_w	fis_efd_icmsipi_arquivo.ds_arquivo_compl%type;


BEGIN
nm_usuario_w := Obter_Usuario_Ativo;
nr_linha_w := nr_linha_p;

select 	count(*) + 1
into STRICT	qt_bloco_w
from 	fis_efd_icmsipi_arquivo
where 	nr_seq_controle = nr_seq_controle_p
and 	substr(cd_registro,1,1) = substr(cd_registro_p,1,1);

if (cd_registro_p = '9990') then
	qt_bloco_w := qt_bloco_w + 1;
end if;

nr_linha_w := nr_linha_w + 1;
ds_linha_w := substr(ds_sep_w || cd_registro_p ||
				ds_sep_w || qt_bloco_w || ds_sep_w,1,8000);
ds_arquivo_w		:= substr(ds_linha_w,1,4000);
ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);

insert into fis_efd_icmsipi_arquivo(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_controle,
		nr_linha,
		ds_arquivo,
		ds_arquivo_compl,
		cd_registro)
	values (
		nextval('fis_efd_icmsipi_arquivo_seq'),
		clock_timestamp(),
		nm_usuario_w,
		clock_timestamp(),
		nm_usuario_w,
		nr_seq_controle_p,
		nr_linha_w,
		ds_arquivo_w,
		ds_arquivo_compl_w,
		cd_registro_p);

nr_linha_p	:= nr_linha_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
