CREATE OR REPLACE PROCEDURE fis_gerar_estrut_trib (nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_estrut_w	fis_estrutura_calculo.nr_sequencia%type;
nr_seq_item_w	fis_estrutura_item.nr_sequencia%type;

C01 CURSOR FOR
SELECT	nr_sequencia
from	fis_estrutura_item
where	nr_seq_estrutura = nr_seq_estrut_w
order by nr_seq_apres, nr_seq_somat, nr_sequencia;


BEGIN

select	nr_seq_estrut_calc
into STRICT	nr_seq_estrut_w
from	ctb_tributo
where	nr_sequencia = nr_sequencia_p;

delete FROM fis_calculo_estrut
where	nr_seq_tributo	= nr_sequencia_p;

open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	insert into fis_calculo_estrut(
		nr_sequencia,
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_estrut,
		nr_seq_item,
		nr_seq_tributo,
		vl_item)
	values (nextval('fis_calculo_estrut_seq'),
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_estrut_w,
		nr_seq_item_w,
		nr_sequencia_p,
		0);
	end;
end loop;
close C01;

commit;

end;
