CREATE OR REPLACE PROCEDURE fis_gerar_hist_nota_fiscal ( nr_seq_nota_p bigint, nm_usuario_p text, cd_evento_p text, cd_texto_historico_p bigint default 0, ds_historico_p text default null, ie_commit_p text default 'S') AS $body$
DECLARE


nr_sequencia_w nota_fiscal_hist.nr_sequencia%type;
ds_historico_w nota_fiscal_hist.ds_historico%type;


BEGIN

  select  nextval('nota_fiscal_hist_seq')
  into STRICT    nr_sequencia_w
;

  if (coalesce(cd_texto_historico_p, 0) > 0) then
    ds_historico_w := wheb_mensagem_pck.get_texto(cd_texto_historico_p);
  else
    ds_historico_w := ds_historico_p;
  end if;

  if (ds_historico_w IS NOT NULL AND ds_historico_w::text <> '') then
    insert into nota_fiscal_hist(
      nr_sequencia,
      dt_atualizacao,
      nm_usuario,
      nr_seq_nota,
      cd_evento,
      ds_historico
    ) values (
      nr_sequencia_w,
      clock_timestamp(),
      nm_usuario_p,
      nr_seq_nota_p,
      cd_evento_p,
      substr(ds_historico_w,1,255)
    );
  end if;

  if (coalesce(ie_commit_p, 'S') = 'S') then
    commit;
  end if;

end;
$body$
