SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_nf_item_w ( nr_seq_transmissao_p bigint, nm_usuario_p text) AS $body$
DECLARE


				
nr_item_nf_w 			fis_nf_item_w.nr_item_nf%type;
vl_unitario_w 			fis_nf_item_w.vl_unitario_item_nf%type;
ds_servico_w 			fis_nf_item_w.ds_servico%type;
nr_seq_nota_w			nota_fiscal.nr_sequencia%type;
cd_ativ_item_w			fis_nf_item_w.cd_atividade%type;
ie_iss_retido_w 		fis_nf_trib_w.ie_iss_retido%type;
ie_imposto_ret_item		varchar(1);
aliquota_item_w 		fis_nf_trib_w.vl_aliquota%type;




c_nota_fiscal_item CURSOR FOR
	SELECT	a.nr_sequencia,
		a.nr_item_nf,
		a.qt_item_nf,
		a.vl_unitario_item_nf,
		a.cd_procedimento,
		a.ie_origem_proced,
		a.cd_material,
		a.cd_estabelecimento
	from	nota_fiscal_item a
	where 	a.nr_sequencia = nr_seq_nota_w;

				
BEGIN

select	a.nr_sequencia
into STRICT 	nr_seq_nota_w
from 	nota_fiscal a,
	nfe_transmissao_nf b
where	b.nr_seq_nota_fiscal	= a.nr_sequencia
and	b.nr_seq_transmissao	= nr_seq_transmissao_p;

for fis_nota_fiscal_item in c_nota_fiscal_item loop
	begin
	
	ds_servico_w:= obter_desc_material(fis_nota_fiscal_item.cd_material);
	if (coalesce(ds_servico_w::text, '') = '') then
	   ds_servico_w:= obter_descricao_procedimento(fis_nota_fiscal_item.cd_procedimento, fis_nota_fiscal_item.ie_origem_proced);
	end if;

	begin
	cd_ativ_item_w:=	obter_dados_grupo_servico_item(obter_item_servico_item_nf(coalesce(fis_nota_fiscal_item.cd_material, 0) , coalesce(fis_nota_fiscal_item.cd_procedimento, 0), coalesce(fis_nota_fiscal_item.ie_origem_proced, 0), 'S'),'GR');
						
	exception
		when others then
		cd_ativ_item_w:= null;
	end;

	ie_imposto_ret_item:=  obter_se_nf_retem_iss(nr_seq_nota_w);
	if (ie_imposto_ret_item = 'S') then
		ie_iss_retido_w:= 1;
	else
		ie_iss_retido_w:= 2;	
	end if;

	begin
	aliquota_item_w := obter_valor_nf_item(fis_nota_fiscal_item.nr_sequencia,fis_nota_fiscal_item.nr_item_nf,'ISS','TX');
	if (coalesce(aliquota_item_w::text, '') = '') then
		aliquota_item_w:= 	obter_valor_nf_item(fis_nota_fiscal_item.nr_sequencia,fis_nota_fiscal_item.nr_item_nf,'ISSNR','TX');
		if (coalesce(aliquota_item_w::text, '') = '') then
			aliquota_item_w:= obter_valor_nf_item(fis_nota_fiscal_item.nr_sequencia,fis_nota_fiscal_item.nr_item_nf,'ISSST','TX');
			if (coalesce(aliquota_item_w::text, '') = '') then
				if (coalesce(aliquota_item_w::text, '') = '') then
					aliquota_item_w:=	0;
				end if;						
			end if;
		end if;
	end if;
	exception
		when others then
		aliquota_item_w:= 0;
	end;


	nr_seq_nota_w:= fis_nota_fiscal_item.nr_sequencia;

	insert into fis_nf_item_w(nr_sequencia,
				nr_seq_nota_fiscal,
				nr_item_nf,
				qt_item_nf,
				vl_unitario_item_nf,
				dt_atualizacao,
				nm_usuario,
				ds_servico,
				nr_seq_transmissao,
				cd_atividade)
			values (nextval('fis_nf_item_w_seq'),
				nr_seq_nota_w,
				fis_nota_fiscal_item.nr_item_nf,
				fis_nota_fiscal_item.qt_item_nf,
				fis_nota_fiscal_item.vl_unitario_item_nf,
				clock_timestamp(),
				nm_usuario_p,
				ds_servico_w,
				nr_seq_transmissao_p,
				cd_ativ_item_w
				);

	insert into fis_nf_trib_w(	nr_sequencia,
				nr_seq_transmissao,
				nr_seq_nota,
				ie_iss_retido,
				vl_aliquota,
				nr_item_nf,
				dt_atualizacao,
				nm_usuario)
			values (nextval('fis_nf_trib_w_seq'),
				nr_seq_transmissao_p,
				nr_seq_nota_w,
				ie_iss_retido_w,
				aliquota_item_w, 
				fis_nota_fiscal_item.nr_item_nf,	
				clock_timestamp(),
				nm_usuario_p
				);					
	end;

end loop;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fis_gerar_nf_item_w ( nr_seq_transmissao_p bigint, nm_usuario_p text) FROM PUBLIC;

