SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_0000_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


ds_fantasia_w			varchar(255);
cd_cnpj_w			varchar(14);
sg_estado_w			pessoa_juridica.sg_estado%type;
cd_inscricao_estadual_w		varchar(20);
cd_municipio_ibge_w		varchar(7);
nr_inscricao_municipal_w		varchar(20);
cd_ind_situacao_especial_w 		varchar(2);
ie_perfil_fisco_w			varchar(15);
nr_cpf_w				varchar(14);
nr_suframa_w			varchar(9);
nr_seq_regra_efd_w		bigint;
nr_versao_efd_w			varchar(5);
tp_registro_w			varchar(4);

nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w			bigint := nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= ds_separador_p;
ie_scp_w			fis_efd_controle.ie_scp%type;
cd_estabelecimento_scp_w	fis_efd_controle.cd_estabelecimento_scp%type;
cd_estab_socio_ost_scp_w	estabelecimento.cd_estab_socio_ost_scp%type;

c01 CURSOR FOR
SELECT	'0000' tp_registro,
	substr(obter_dados_pf_pj(null, a.cd_cgc, 'N'),1,100) ds_fantasia,
	Elimina_Caracteres_Especiais(a.cd_cgc) cd_cnpj,
	substr(obter_dados_pf_pj(null, a.cd_cgc, 'UF'),1,2) sg_estado,
	Elimina_Caracteres_Especiais(a.cd_inscricao_estadual),
	Elimina_Caracteres_Especiais(substr(b.cd_municipio_ibge || substr(calcula_digito('MODULO10', b.cd_municipio_ibge),1,1),1,7)) cd_municipio_ibge,
	a.cd_inscricao_municipal nr_inscricao_municipal,
	'1' cd_ind_situacao_especial
from	pessoa_juridica b,
	estabelecimento a
where	a.cd_cgc = b.cd_cgc
and	((ie_scp_w = 'N' AND a.cd_estabelecimento = cd_estabelecimento_p) or
	(ie_scp_w = 'S' AND a.cd_estabelecimento = cd_estab_socio_ost_scp_w));


BEGIN

select	max(nr_seq_regra_efd),
	max(ie_scp),
	max(cd_estabelecimento_scp)	
into STRICT	nr_seq_regra_efd_w,
	ie_scp_w,
	cd_estabelecimento_scp_w
from	fis_efd_controle
where	nr_sequencia	= nr_seq_controle_p;

if ie_scp_w = 'S' then
	select	max(cd_estab_socio_ost_scp)
	into STRICT	cd_estab_socio_ost_scp_w
	from	estabelecimento
	where	cd_estabelecimento = cd_estabelecimento_scp_w;
end if;	

select	lpad(max(a.nr_versao_efd),3,0),
		max(a.ie_perfil_fisco)
into STRICT	nr_versao_efd_w,
	ie_perfil_fisco_w
from	fis_regra_efd a
where	a.nr_sequencia	= nr_seq_regra_efd_w;

open C01;
loop
fetch C01 into	
	tp_registro_w,
	ds_fantasia_w,			
	cd_cnpj_w,			
	sg_estado_w,			
	cd_inscricao_estadual_w,		
	cd_municipio_ibge_w,		
	nr_inscricao_municipal_w,		
	cd_ind_situacao_especial_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	ds_linha_w	:= substr(	sep_w || tp_registro_w				||
					sep_w || nr_versao_efd_w			||
					sep_w || '0'					||
					sep_w || to_char(dt_inicio_p,'ddmmyyyy')	||
					sep_w || to_char(fim_mes(dt_inicio_p),'ddmmyyyy')	||
					sep_w || ds_fantasia_w  			||
					sep_w || cd_cnpj_w  				||
					sep_w || nr_cpf_w				||
					sep_w || sg_estado_w 				||
					sep_w || cd_inscricao_estadual_w  		||
					sep_w || cd_municipio_ibge_w  			||
					sep_w || nr_inscricao_municipal_w 		||
					sep_w || nr_suframa_w				||
					sep_w || ie_perfil_fisco_w			|| 
					sep_w || cd_ind_situacao_especial_w 		|| sep_w,1,8000);
	
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_efd_arquivo(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_controle_efd,
		nr_linha,
		cd_registro,
		ds_arquivo,
		ds_arquivo_compl)
	values (	nr_seq_registro_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_controle_p,
		nr_linha_w,
		'0000',
		ds_arquivo_w,
		ds_arquivo_compl_w);
	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fis_gerar_reg_0000_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

