SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_0000_icmsipi (nr_seq_controle_p bigint) AS $body$
DECLARE


-- VARIABLES
ie_gerou_dados_bloco_w 	varchar(1) := 'N';
			
-- USUARIO
nm_usuario_w			usuario.nm_usuario%type;

c01 CURSOR FOR
	SELECT	'0000' cd_reg,
			a.cd_ver,
			CASE WHEN a.ie_retificadora='N' THEN  0 WHEN a.ie_retificadora='S' THEN  1 END  ie_retificadora,
			trunc(a.dt_inicio_apuracao) dt_ini,
			trunc(a.dt_fim_apuracao) dt_fin,
			substr(obter_dados_pf_pj(null, b.cd_cgc, 'N'),1,100) ds_nome,
			elimina_caracteres_especiais(b.cd_cgc) cd_cnpj,
			substr(obter_dados_pf_pj(null, b.cd_cgc, 'UF'),1,2) sg_uf,
			substr(CASE WHEN upper(c.nr_inscricao_estadual)='ISENTA' THEN  '' WHEN upper(c.nr_inscricao_estadual)='ISENTO' THEN  ''  ELSE elimina_caractere_especial(c.nr_inscricao_estadual) END , 1, 14) nr_ie,
			elimina_caracteres_especiais(substr(c.cd_municipio_ibge || substr(calcula_digito('MODULO10', c.cd_municipio_ibge),1,1),1,7)) cd_mun,
			b.cd_inscricao_municipal ds_im,
			a.ie_ind_perfil ds_ind_perfil,
			a.ie_ind_ativ ds_ind_ativ
	from	fis_efd_icmsipi_controle a,
			estabelecimento b,
			pessoa_juridica c				
	where	b.cd_cgc = c.cd_cgc
	and		a.nr_sequencia = nr_seq_controle_p
	and		b.cd_estabelecimento = a.cd_estabelecimento;
		
BEGIN

nm_usuario_w := Obter_Usuario_Ativo;

for r01 in c01 loop
	insert into fis_efd_icmsipi_0000(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_controle,
		cd_reg,
		cd_ver,
		cd_fin,
		dt_ini,
		dt_fin,
		ds_nome,
		cd_cnpj,
		cd_cpf,
		sg_uf,
		nr_ie,
		cd_mun,
		ds_im,
		ds_suframa,
		ds_ind_perfil,
		ds_ind_ativ)
	values (
		nextval('fis_efd_icmsipi_0000_seq'),
		clock_timestamp(),
		nm_usuario_w,
		clock_timestamp(),
		nm_usuario_w,
		nr_seq_controle_p,
		r01.cd_reg,
		r01.cd_ver,
		r01.ie_retificadora,
		r01.dt_ini,
		r01.dt_fin,
		r01.ds_nome,
		r01.cd_cnpj,
		null,
		r01.sg_uf,
		r01.nr_ie,
		r01.cd_mun,
		r01.ds_im,
		null,
		r01.ds_ind_perfil,
		r01.ds_ind_ativ);
		
	ie_gerou_dados_bloco_w := 'S';
end loop;

if (ie_gerou_dados_bloco_w = 'S') then
	update 	fis_efd_icmsipi_controle
	set		ie_mov_0 = 'S'
	where 	nr_sequencia = nr_seq_controle_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
