CREATE OR REPLACE PROCEDURE fis_gerar_reg_0200_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE

 
nr_seq_regra_efd_w		bigint;
nr_versao_efd_w			varchar(5);
tp_registro_w			varchar(4);
nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w			bigint := nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= ds_separador_p;

c01 CURSOR FOR 
SELECT	'0200' tp_registro, 
	a.cd_material, 
	trim(both a.ds_material) ds_material, 
	substr(obter_dados_material_estab(a.cd_material,cd_estabelecimento_p,'UME'),1,30) cd_unidade_medida_estoque, 
	b.ie_tipo_fiscal, 
	substr(a.cd_classif_fiscal,1,8) cd_classif_fiscal 
from	material_fiscal b, 
	material a 
where	a.ie_situacao	= 'A' 
and	a.cd_material	= b.cd_material 
order by	2;

vet01	C01%RowType;


BEGIN 
 
open C01;
loop 
fetch C01 into	 
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	 
	ds_linha_w	:= substr(	sep_w || vet01.tp_registro		 	|| 
				sep_w || vet01.cd_material		 	|| 
				sep_w || vet01.ds_material		 	|| 
				sep_w || ''				 	|| 
				sep_w || ''				 	|| 
				sep_w || vet01.cd_unidade_medida_estoque	|| 
				sep_w || vet01.ie_tipo_fiscal 		 	|| 
				sep_w || vet01.cd_classif_fiscal	 	|| 
				sep_w || ''				 	|| 
				sep_w || ''				 	|| 
				sep_w || ''				 	|| 
				sep_w || ''				 	|| sep_w,1,8000);
	 
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w		:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;
 
	insert into fis_efd_arquivo( 
		nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec, 
		nr_seq_controle_efd, 
		nr_linha, 
		cd_registro, 
		ds_arquivo, 
		ds_arquivo_compl) 
	values (	nr_seq_registro_w, 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_controle_p, 
		nr_linha_w, 
		vet01.tp_registro, 
		ds_arquivo_w, 
		ds_arquivo_compl_w);
	end;
end loop;
close C01;
 
commit;
 
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	
 
end;
$body$
