SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_0450_icmsipi (nr_seq_controle_p bigint) AS $body$
DECLARE


/*REGISTRO 0450: TABELA DE INFORMAÇÃO COMPLEMENTAR DO DOCUMENTO FISCAL*/



-- VARIABLES
ie_gerou_dados_bloco_w varchar(1) := 'N';

nr_seq_icmsipi_0450_w fis_efd_icmsipi_0450.nr_sequencia%type;

qt_cursor_w bigint := 0;
nr_vetor_w  bigint := 0;

-- USUARIO
nm_usuario_w usuario.nm_usuario%type;

/*Cursor que retorna as informações para o registro 0450 restringindo pela sequencia da nota fiscal*/
c_reg_0450 CURSOR FOR
SELECT	distinct cd_inf,
	fis_efd_icmsipi_elimina_c(b.ds_observacao) ds_txt
from (	SELECT	cd_inf,
			nr_seq_nota
		from	fis_efd_icmsipi_C110
		where	nr_seq_controle	= nr_seq_controle_p
		
union all

		select	cd_inf,
			nr_seq_nota
		from	fis_efd_icmsipi_C500
		where	nr_seq_controle	= nr_seq_controle_p
		
union all

		select	cd_inf,
			nr_seq_nota
		from	fis_efd_icmsipi_D100
		where	nr_seq_controle	= nr_seq_controle_p
		
union all

		select	cd_inf,
			nr_seq_nota
		from	fis_efd_icmsipi_D500
		where	nr_seq_controle	= nr_seq_controle_p) a,
	nota_fiscal b
where	a.nr_Seq_nota	= b.nr_Sequencia
and	(b.ds_observacao IS NOT NULL AND b.ds_observacao::text <> '')
--and not(ascii(b.ds_observacao) in(10, 13))
order by 1;

/*Criação do array com o tipo sendo do cursor eespecificado - c_reg_0450*/

type reg_c_reg_0450 is table of c_reg_0450%RowType;
vetReg0450 reg_c_reg_0450;

/*Criação do array com o tipo sendo da tabela eespecificada - FIS_EFD_ICMSIPI_0450 */

type registro is table of fis_efd_icmsipi_0450%rowtype index by integer;
fis_registros_w registro;

BEGIN
/*Obteção do usuário ativo no tasy*/

nm_usuario_w := Obter_Usuario_Ativo;

open c_reg_0450;
loop
fetch c_reg_0450 bulk collect into vetReg0450 limit 1000;
	for i in 1 .. vetReg0450.Count loop
		begin

		/*Incrementa a variavel para o array*/

		qt_cursor_w:=	qt_cursor_w + 1;

		if (ie_gerou_dados_bloco_w = 'N') then
			ie_gerou_dados_bloco_w := 'S';
		end if;

		/*Busca da sequencia da tabela especificada - fis_efd_icmsipi_0450 */

		select	nextval('fis_efd_icmsipi_0450_seq')
		into STRICT	nr_seq_icmsipi_0450_w
		;

		/*Inserindo valores no array para realização do forall posteriormente*/
		fis_registros_w[qt_cursor_w].nr_sequencia 		:= nr_seq_icmsipi_0450_w;
		fis_registros_w[qt_cursor_w].dt_atualizacao 		:= clock_timestamp();
		fis_registros_w[qt_cursor_w].nm_usuario 		:= nm_usuario_w;
		fis_registros_w[qt_cursor_w].dt_atualizacao_nrec 	:= clock_timestamp();
		fis_registros_w[qt_cursor_w].nm_usuario_nrec 		:= nm_usuario_w;
		fis_registros_w[qt_cursor_w].cd_reg 			:= '0450';
		fis_registros_w[qt_cursor_w].cd_inf 			:= vetReg0450[i].cd_inf;
		fis_registros_w[qt_cursor_w].ds_txt 			:= vetReg0450[i].ds_txt;
		fis_registros_w[qt_cursor_w].nr_seq_controle 		:= nr_seq_controle_p;

		if (nr_vetor_w >= 1000) then
			begin
			/*Inserindo registros definitivamente na tabela especifica - FIS_EFD_ICMSIPI_0450 */

			forall i in fis_registros_w.first .. fis_registros_w.last
				insert into fis_efd_icmsipi_0450 values fis_registros_w(i);

			nr_vetor_w := 0;
			fis_registros_w.delete;

			commit;

			end;
		end if;

		/*incrementa variavel para realizar o forall quando chegar no valor limite*/

		nr_vetor_w := nr_vetor_w + 1;

		end;
	end loop;
EXIT WHEN NOT FOUND; /* apply on c_reg_0450 */
end loop;
close c_reg_0450;

if (fis_registros_w.count > 0) then
	begin
	/*Inserindo registro que não entraram outro for all devido a quantidade de registros no vetor*/

	forall i in fis_registros_w.first .. fis_registros_w.last
		insert into fis_efd_icmsipi_0450 values fis_registros_w(i);

	fis_registros_w.delete;

	commit;

	end;
end if;

/*Libera memoria*/

dbms_session.free_unused_user_memory;

/*Atualização informação no controle de geração de registro para SIM*/

if (ie_gerou_dados_bloco_w = 'S') then
	update	fis_efd_icmsipi_controle
	set	ie_mov_0 = 'S'
	where	nr_sequencia = nr_seq_controle_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
