CREATE OR REPLACE PROCEDURE fis_gerar_reg_1010_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


cd_empresa_w			smallint;
nr_seq_regra_sped_w		bigint;
nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w		bigint := nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= ds_separador_p;
dt_fim_w			timestamp;
qt_registros_w			bigint;


BEGIN

ds_linha_w	:= substr(	sep_w || '1010'	||
				sep_w || 'N'	||
				sep_w || 'N'	||
				sep_w || 'N'	||
				sep_w || 'N'	||
				sep_w || 'N'	||
				sep_w || 'N'	||
				sep_w || 'S'	||
				sep_w || 'N'	||
				sep_w || 'N'    || sep_w, 1, 8000);

ds_arquivo_w			:= substr(ds_linha_w,1,4000);
ds_arquivo_compl_w		:= substr(ds_linha_w,4001,4000);
nr_seq_registro_w		:= nr_seq_registro_w + 1;
nr_linha_w			:= nr_linha_w + 1;

insert into fis_efd_arquivo(
	nr_sequencia,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec,
	nr_seq_controle_efd,
	nr_linha,
	cd_registro,
	ds_arquivo,
	ds_arquivo_compl)
values (	nr_seq_registro_w,
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nr_seq_controle_p,
	nr_linha_w,
	'1010',
	ds_arquivo_w,
	ds_arquivo_compl_w);

commit;

cd_empresa_w := obter_empresa_estab(cd_estabelecimento_p);

select	max(nr_seq_regra_efd)
into STRICT	nr_seq_regra_sped_w
from	fis_efd_controle
where	nr_sequencia	= nr_seq_controle_p;

select	count(*)
into STRICT	qt_registros_w
from	fis_regra_efd_reg
where	nr_seq_regra_efd = nr_seq_regra_sped_w
and	ie_gerar = 'S'
and	cd_registro = '0150';

if (qt_registros_w > 0) then
	SELECT * FROM fis_gerar_1600_efd_icms(nr_seq_controle_p, nm_usuario_p, cd_estabelecimento_p, dt_inicio_p, dt_fim_p, cd_empresa_w, ds_separador_p, nr_linha_w, nr_seq_registro_w) INTO STRICT nr_linha_w, nr_seq_registro_w;
end if;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
