SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_1010_icmsipi (nr_seq_controle_p bigint) AS $body$
DECLARE


-- VARIABLES
ie_gerou_dados_bloco_w 	varchar(1) := 'N';

nm_usuario_w					usuario.nm_usuario%type;
cd_cgc_w 						estabelecimento.cd_cgc%type;
cd_ver_w						fis_efd_icmsipi_controle.cd_ver%type;
ie_ind_giaf_w 					fis_efd_icmsipi_1010.ie_ind_giaf1%type := null;
ie_rest_ressarc_compl_icms_w 	fis_efd_icmsipi_1010.ie_rest_ressarc_compl_icms%type := null;

-- FIS_EFD_ICMSIPI_CONTROLE
nr_seq_lote_w			fis_efd_icmsipi_controle.nr_seq_lote%type;

/*
cursor c01 is
	select	'1010' cd_reg,
			ie_1100 ie_ind_exp,
			ie_1200 ie_ind_ccrf,
			ie_1300 ie_ind_comb,
			ie_1390 ie_ind_usina,
			ie_1400 ie_ind_va,
			ie_1500 ie_ind_ee,
			ie_1600 ie_ind_cart,
			ie_1700 ie_ind_form,
			ie_1800 ie_ind_aer
	from	fis_efd_icmsipi_regra_1010 a
	where	a.nr_seq_lote = nr_seq_lote_w;
*/
c01 CURSOR FOR
	SELECT 	'1010' cd_reg,
			'N' ie_ind_exp,
			'N' ie_ind_ccrf,
			'N' ie_ind_comb,
			'N' ie_ind_usina,
			'N' ie_ind_va,
			'N' ie_ind_ee,
			'N' ie_ind_cart,
			'N' ie_ind_form,
			'N' ie_ind_aer
	;
		
BEGIN

nm_usuario_w := Obter_Usuario_Ativo;

select 	b.cd_cgc,
		a.cd_ver
into STRICT 	cd_cgc_w,
		cd_ver_w
from 	fis_efd_icmsipi_controle a,
		estabelecimento b
where 	a.cd_estabelecimento = b.cd_estabelecimento
and 	a.nr_sequencia = nr_seq_controle_p;

/*Somente para pernambuco*/

if ((cd_ver_w)::numeric  > 12) then
	ie_ind_giaf_w := 'N';
	
	/*if (obter_dados_pf_pj(null, cd_cgc_w, 'UF') = 'PE') then
		ie_ind_giaf_w := 'S';
	else
		ie_ind_giaf_w := 'N';
	end if;*/
end if;

/* Padrão 'N' para o Tasy a partir da versão 014. */

if (cd_ver_w)::numeric  > 13 then
	ie_rest_ressarc_compl_icms_w := 'N';
end if;

select 	nr_seq_lote
into STRICT	nr_seq_lote_w
from	fis_efd_icmsipi_controle
where 	nr_sequencia = nr_seq_controle_p;

for r01 in c01 loop
	insert into fis_efd_icmsipi_1010(
		nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_controle,
		cd_reg,
		ie_ind_exp,
		ie_ind_ccrf,
		ie_ind_comb,
		ie_ind_usina,
		ie_ind_va,
		ie_ind_ee,
		ie_ind_cart,
		ie_ind_form,
		ie_ind_aer,
		ie_ind_giaf1,
		ie_ind_giaf3,
		ie_ind_giaf4,
		ie_rest_ressarc_compl_icms)
	values (
		nextval('fis_efd_icmsipi_1010_seq'),
		clock_timestamp(),
		nm_usuario_w,
		clock_timestamp(),
		nm_usuario_w,
		nr_seq_controle_p,
		r01.cd_reg,
		r01.ie_ind_exp,
		r01.ie_ind_ccrf,
		r01.ie_ind_comb,
		r01.ie_ind_usina,
		r01.ie_ind_va,
		r01.ie_ind_ee,
		r01.ie_ind_cart,
		r01.ie_ind_form,
		r01.ie_ind_aer,
		ie_ind_giaf_w,
		ie_ind_giaf_w,
		ie_ind_giaf_w,
		ie_rest_ressarc_compl_icms_w);
		
	ie_gerou_dados_bloco_w := 'S';
end loop;

if (ie_gerou_dados_bloco_w = 'S') then
	update 	fis_efd_icmsipi_controle
	set		ie_mov_1 = 'S'
	where 	nr_sequencia = nr_seq_controle_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
