CREATE OR REPLACE PROCEDURE fis_gerar_reg_9900_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_efd_w		bigint;
nr_versao_efd_w			varchar(5);
tp_registro_w			varchar(4)	:= '9900';

nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w			bigint := nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= ds_separador_p;
qt_linhas_w			bigint;

c01 CURSOR FOR
SELECT	*
from (
	SELECT	a.cd_registro cd_registro,
		count(*) qt_linhas
	from	fis_efd_arquivo a
	where	nr_seq_controle_efd	= nr_seq_controle_p
	group by a.cd_registro
	
union

	select	a.cd_registro cd_registro,
		1 qt_linhas
	from	fis_regra_efd_reg a
	where	a.cd_registro in ('9900','9990','9999')
	and	a.nr_seq_regra_efd	= nr_seq_regra_efd_w ) alias2
order by 	cd_registro COLLATE "WEST_EUROPEAN";

vet01	C01%RowType;


BEGIN

select	max(nr_seq_regra_efd)
into STRICT	nr_seq_regra_efd_w
from	fis_efd_controle
where	nr_sequencia	= nr_seq_controle_p;


open C01;
loop
fetch C01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_linhas_w	:= vet01.qt_linhas;

	/*Contar os registros do pr√≥prio bloco */

	if (vet01.cd_registro	= tp_registro_w) then

		select	count(*)
		into STRICT	qt_linhas_w
		from	fis_efd_arquivo a
		where	nr_seq_controle_efd	= nr_seq_controle_p
		and	cd_registro		= tp_registro_w;

		qt_linhas_w	:= qt_linhas_w	+ 3;
	end if;

	ds_linha_w	:= substr(	sep_w || tp_registro_w	||
				sep_w || vet01.cd_registro	||
				sep_w || qt_linhas_w	|| sep_w,1,8000);

	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w		:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_efd_arquivo(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_controle_efd,
		nr_linha,
		cd_registro,
		ds_arquivo,
		ds_arquivo_compl)
	values (	nr_seq_registro_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_controle_p,
		nr_linha_w,
		tp_registro_w,
		ds_arquivo_w,
		ds_arquivo_compl_w);
	end;
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
