CREATE OR REPLACE PROCEDURE fis_gerar_reg_a010_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_seq_regra_efd_w	bigint;
nr_versao_efd_w		varchar(5);
tp_registro_w		varchar(4);
nr_linha_w		bigint := qt_linha_p;
nr_seq_registro_w		bigint := nr_sequencia_p;
ds_arquivo_w		varchar(4000);
ds_arquivo_compl_w	varchar(4000);
ds_linha_w		varchar(8000);
sep_w			varchar(1) := ds_separador_p;
cd_empresa_w		smallint;
ie_scp_w			fis_efd_controle.ie_scp%type;
cd_estabelecimento_scp_w	fis_efd_controle.cd_estabelecimento_scp%type;

c01 CURSOR FOR
SELECT	'A010' tp_registro,
	CASE WHEN ie_scp_w='N' THEN  a.cd_cgc  ELSE obter_cnpj_estabelecimento(cd_estab_socio_ost_scp) END  cd_cgc,
	a.cd_estabelecimento	
from	estabelecimento a,
	pessoa_juridica	d
where	a.cd_cgc	= d.cd_cgc
and	a.cd_empresa = cd_empresa_p
and	d.ie_situacao	= 'A'
and	a.ie_situacao	= 'A'
and	coalesce(a.ie_gerar_sped,'S') = 'S'
and	((ie_scp_w = 'N' AND a.ie_scp = 'N') or
	(ie_scp_w = 'S' AND a.cd_estabelecimento = cd_estabelecimento_scp_w));

vet01	C01%RowType;


BEGIN

select	max(ie_scp),
	max(cd_estabelecimento_scp)
into STRICT	ie_scp_w,
	cd_estabelecimento_scp_w
from	fis_efd_controle
where	nr_sequencia	= nr_seq_controle_p;

open C01;
loop
fetch C01 into	
	vet01;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin	
	ds_linha_w	:= substr(	sep_w || vet01.tp_registro	||
				sep_w || vet01.cd_cgc	|| sep_w,1,8000);
	
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w		:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;

	insert into fis_efd_arquivo(
					nr_sequencia,
					nm_usuario,
					dt_atualizacao,
					nm_usuario_nrec,
					dt_atualizacao_nrec,
					nr_seq_controle_efd,
					nr_linha,
					cd_registro,
					ds_arquivo,
					ds_arquivo_compl)
				values (
					nr_seq_registro_w,
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nr_seq_controle_p,
					nr_linha_w,
					vet01.tp_registro,
					ds_arquivo_w,
					ds_arquivo_compl_w);
	end;
	
	SELECT * FROM fis_gerar_reg_A100_efd(	nr_seq_controle_p, nm_usuario_p, vet01.cd_estabelecimento, dt_inicio_p, dt_fim_p, cd_empresa_p, ds_separador_p, nr_linha_w, nr_seq_registro_w) INTO STRICT nr_linha_w, nr_seq_registro_w;
	
end loop;
close C01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
