SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_abertura_efd ( nr_seq_controle_p bigint, nm_usuario_p text, ds_separador_p text, cd_registro_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


cd_registro_w			varchar(15)	:= cd_registro_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
nr_linha_w			bigint	:= qt_linha_p;
nr_seq_registro_w			bigint 	:= nr_sequencia_p;
nr_seq_regra_efd_w		bigint;
sep_w				varchar(1)	:= ds_separador_p;
ie_ind_movimento_w		varchar(1);
qt_registros_w			bigint;


BEGIN
/*
ie_ind_movimento_w		:= '0';

if	(cd_registro_w in ('C001','D001','E001','G001','1001')) then
	ie_ind_movimento_w	:= '1';
end if;
*/
select	nr_seq_regra_efd
into STRICT	nr_seq_regra_efd_w
from	fis_efd_controle
where	nr_sequencia = nr_seq_controle_p;

select	count(*)
into STRICT	qt_registros_w
from	fis_regra_efd_reg
where	substr(CD_REGISTRO,1,1) = substr(cd_registro_p,1,1)
and	ie_gerar = 'S'
and	nr_seq_regra_efd = nr_seq_regra_efd_w;

if (qt_registros_w > 2) then		-- Maior do que dois porque sempre vai ter marcado o de abertura e de encerramento
	ie_ind_movimento_w	:= 0;
else
	ie_ind_movimento_w	:= 1;
end if;

ds_linha_w		:= substr(	sep_w || cd_registro_w		||
				sep_w || ie_ind_movimento_w	|| sep_w ,1,8000);

ds_arquivo_w		:= substr(ds_linha_w,1,4000);
nr_seq_registro_w		:= nr_seq_registro_w + 1;
nr_linha_w		:= nr_linha_w + 1;

insert into fis_efd_arquivo(
	nr_sequencia,
	nm_usuario,
	dt_atualizacao,
	nm_usuario_nrec,
	dt_atualizacao_nrec,
	nr_seq_controle_efd,
	nr_linha,
	cd_registro,
	ds_arquivo,
	ds_arquivo_compl)
values (	nr_seq_registro_w,
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nr_seq_controle_p,
	nr_linha_w,
	cd_registro_w,
	ds_arquivo_w,
	ds_arquivo_compl_w);

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
