CREATE OR REPLACE PROCEDURE fis_gerar_reg_apuracao_ecf ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_registros_w			fis_registros_ecf.ds_registros%type;
ds_registros_compl_w		fis_registros_ecf.ds_registros_compl%type;

c01 CURSOR FOR
	SELECT	a.ds_registros,
		a.ds_registros_compl
	from	fis_registros_ecf a
	where	a.ie_situacao = 'A'
	order by 	a.nr_sequencia;


BEGIN

open c01;
loop
fetch c01 into
	ds_registros_w,
	ds_registros_compl_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
		insert into fis_reg_apuracao_ecf(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ds_registro_ecf,
			nr_seq_lote,
			ie_gerar,
			ds_compl_reg_ecf)
		values (	nextval('fis_reg_apuracao_ecf_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			ds_registros_w,
			nr_sequencia_p,
			'S',
			ds_registros_compl_w);

	end;
end loop;
close c01;

commit;

end;
