SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_c140_icmsipi ( nr_seq_controle_p bigint, nr_seq_nota_p bigint) AS $body$
DECLARE


/*REGISTRO C140: CUPOM FISCAL REFERENCIADO..*/

-- VARIABLES
ie_gerou_dados_bloco_w varchar(1) := 'N';

nr_seq_icmsipi_C140_w fis_efd_icmsipi_C140.nr_sequencia%type;

qt_cursor_w bigint := 0;
nr_vetor_w  bigint := 0;

-- USUARIO
nm_usuario_w usuario.nm_usuario%type;

/*Cursor que retorna as informações para o registro C140 restringindo pela sequencia da nota fiscal*/

c_reg_C140 CURSOR FOR
SELECT	1 cd_ind_emit,
	'0' cd_ind_tit,
	null ds_tit,
	a.nr_nota_fiscal nr_tit,
	c.qt_nota_venc qt_parc,
	c.vl_parc vl_tit
from	nota_fiscal a,
	(	select	count(1) qt_nota_venc,
			sum(vl_parc) vl_parc,
			b.nr_seq_nota
		from	fis_efd_icmsipi_C141 b
		group by b.nr_seq_nota) c
where	a.nr_sequencia	= c.nr_seq_nota
and 	nr_sequencia	= nr_seq_nota_p;

/*Criação do array com o tipo sendo do cursor eespecificado - c_reg_C140*/

type reg_c_reg_C140 is table of c_reg_C140%RowType;
vetRegC140 reg_c_reg_C140;

/*Criação do array com o tipo sendo da tabela eespecificada - FIS_EFD_ICMSIPI_C140 */

type registro is table of fis_efd_icmsipi_C140%rowtype index by integer;
fis_registros_w registro;

BEGIN
/*Obteção do usuário ativo no tasy*/

nm_usuario_w := Obter_Usuario_Ativo;

open c_reg_C140;
loop
fetch c_reg_C140 bulk collect into vetRegC140 limit 1000;
	for i in 1 .. vetRegC140.Count loop
		begin

		/*Incrementa a variavel para o array*/

		qt_cursor_w:=	qt_cursor_w + 1;

		if (ie_gerou_dados_bloco_w = 'N') then
			ie_gerou_dados_bloco_w := 'S';
		end if;

		/*Busca da sequencia da tabela especificada - fis_efd_icmsipi_C140 */

		select	nextval('fis_efd_icmsipi_c140_seq')
		into STRICT	nr_seq_icmsipi_C140_w
		;

		/*Inserindo valores no array para realização do forall posteriormente*/

		fis_registros_w[qt_cursor_w].nr_sequencia 		:= nr_seq_icmsipi_C140_w;
		fis_registros_w[qt_cursor_w].dt_atualizacao 		:= clock_timestamp();
		fis_registros_w[qt_cursor_w].nm_usuario 		:= nm_usuario_w;
		fis_registros_w[qt_cursor_w].dt_atualizacao_nrec 	:= clock_timestamp();
		fis_registros_w[qt_cursor_w].nm_usuario_nrec 		:= nm_usuario_w;
		fis_registros_w[qt_cursor_w].cd_reg 			:= 'C140';
		fis_registros_w[qt_cursor_w].cd_ind_emit 		:= vetRegC140[i].cd_ind_emit;
		fis_registros_w[qt_cursor_w].cd_ind_tit 		:= vetRegC140[i].cd_ind_tit;
		fis_registros_w[qt_cursor_w].ds_tit 			:= vetRegC140[i].ds_tit;
		fis_registros_w[qt_cursor_w].nr_tit 			:= vetRegC140[i].nr_tit;
		fis_registros_w[qt_cursor_w].qt_parc 			:= vetRegC140[i].qt_parc;
		fis_registros_w[qt_cursor_w].vl_tit 			:= vetRegC140[i].vl_tit;
		fis_registros_w[qt_cursor_w].nr_seq_controle 		:= nr_seq_controle_p;
		fis_registros_w[qt_cursor_w].nr_seq_nota 		:= nr_seq_nota_p;

		if (nr_vetor_w >= 1000) then
			begin
			/*Inserindo registros definitivamente na tabela especifica - FIS_EFD_ICMSIPI_C140 */

			forall i in fis_registros_w.first .. fis_registros_w.last
				insert into fis_efd_icmsipi_C140 values fis_registros_w(i);

			nr_vetor_w := 0;
			fis_registros_w.delete;

			commit;

			end;
		end if;

		/*incrementa variavel para realizar o forall quando chegar no valor limite*/

		nr_vetor_w := nr_vetor_w + 1;

		end;
	end loop;
EXIT WHEN NOT FOUND; /* apply on c_reg_C140 */
end loop;
close c_reg_C140;

if (fis_registros_w.count > 0) then
	begin
	/*Inserindo registro que não entraram outro for all devido a quantidade de registros no vetor*/

	forall i in fis_registros_w.first .. fis_registros_w.last
		insert into fis_efd_icmsipi_C140 values fis_registros_w(i);

	fis_registros_w.delete;

	commit;

	end;
end if;

/*Libera memoria*/

dbms_session.free_unused_user_memory;

/*Atualização informação no controle de geração de registro para SIM*/

if (ie_gerou_dados_bloco_w = 'S') then
	update	fis_efd_icmsipi_controle
	set	ie_mov_C = 'S'
	where	nr_sequencia = nr_seq_controle_p;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
