SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_c170_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, nr_seq_superior_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE

 
nr_seq_regra_efd_w		bigint;
cd_tributo_pis_w		smallint;
cd_tributo_cofins_w		smallint;
nr_versao_efd_w			varchar(5);
cd_st_pis_w			varchar(15);
cd_st_cofins_w			varchar(15);
tp_registro_w			varchar(4);
nr_linha_w			bigint	:= qt_linha_p;
nr_seq_registro_w		bigint	:= nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= ds_separador_p;
ie_tipo_nota_w			varchar(1);
nr_item_seq_w			bigint	:= 0; --número sequencial dos itens da nota, precisa gerar 1,2,3... e as vezes na nota está 2,4,6... 
vl_pis_w			double precision	:= 0;
vl_cofins_w			double precision	:= 0;

/* 
obs 001 - O campo cd_enquadramento_ipi (COD_ENQ) é nulo. 
conforme consulta no site da receita, esta tabela ainda não foi criada pelo governo. 
deve ser gerado o campo nulo 
 
*/
 
 
c_valores CURSOR FOR 
	SELECT	distinct 
		'C170' tp_registro, 
		b.nr_item_nf, 
		b.cd_material, 
		b.qt_item_nf, 
		b.cd_unidade_medida_compra, 
		b.vl_total_item_nf, 
		b.vl_desconto, 
		1 ie_movimento, 
		substr(c.ie_origem_mercadoria, 1, 1) || substr(c.ie_tributacao_icms, 1, 2) cd_st_icms, 
		substr(Elimina_Caracter(obter_dados_natureza_operacao(a.cd_natureza_operacao, 'CF'), '.'), 1, 4) cd_cfop, 
		a.cd_natureza_operacao, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'ICMS', 'BC') vl_base_icms, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'ICMS', 'TX') vl_aliquota_icms, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'ICMS', 'TRIB') vl_icms_deb_cred, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'ICMSST', 'BC') vl_base_icms_st, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'ICMSST', 'TX') vl_aliquota_st, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'ICMSST', 'TRIB') vl_icms_st, 
		0 ie_indicador_apuracao, 
		substr(c.ie_tributacao_ipi, 1, 2) cd_cst_ipi, 
		null cd_enquadramento_ipi, /* olhar obs no inicio da procedure 001*/
 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'IPI', 'BC') vl_base_ipi, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'IPI', 'TX') vl_aliquota_ipi, 
		nfe_obter_valor_trib_item(b.nr_sequencia, b.nr_item_nf, 'IPI', 'TRIB') vl_deb_cred_ipi, 
		lpad(coalesce(c.ie_tributacao_pis,'0'),2,'0') cd_st_pis, 
		(SELECT	coalesce(sum(x.vl_base_calculo),0) 
		from	nota_fiscal_item_trib	x, 
			tributo			w 
		where	w.cd_tributo		= x.cd_tributo 
		and	x.nr_sequencia		= a.nr_sequencia 
		and	x.nr_item_nf		= b.nr_item_nf 
		and 	w.ie_tipo_tributo	= 'PIS' 
		and	coalesce(x.ie_rateio,'N')	= 'N') vl_base_pis, 
		--a.vl_mercadoria vl_base_pis, 
		(select	max(x.tx_tributo) 
		from	nota_fiscal_item_trib	x, 
			tributo			w 
		where	w.cd_tributo		= x.cd_tributo 
		and	x.nr_sequencia		= a.nr_sequencia 
		and	x.nr_item_nf		= b.nr_item_nf 
		and 	w.ie_tipo_tributo	= 'PIS' 
		and	coalesce(x.ie_rateio,'N')	= 'N') pr_aliquota_pis, 
		--substr(replace(campo_mascara(obter_pr_imposto(cd_tributo_pis_w),2),'.',','),1,15) pr_aliquota_pis,	 
		null qt_base_pis, 
		null vl_aliquota_pis, 
		(select	coalesce(sum(x.vl_tributo),0) 
		from	nota_fiscal_item_trib	x, 
			tributo			w 
		where	w.cd_tributo		= x.cd_tributo 
		and	x.nr_sequencia		= a.nr_sequencia 
		and	x.nr_item_nf		= b.nr_item_nf 
		and 	w.ie_tipo_tributo	= 'PIS' 
		and	coalesce(x.ie_rateio,'N')	= 'N') vl_pis, 
		--substr(replace(campo_mascara((obter_vl_tributo_item(b.nr_sequencia, b.nr_item_nf, cd_tributo_pis_w, 'VT')),2),'.',','),1,15) vl_pis,	 
		lpad(coalesce(c.ie_tributacao_cofins,'0'),2,'0') cd_st_cofins, 
		(select	coalesce(sum(x.vl_base_calculo),0) 
		from	nota_fiscal_item_trib	x, 
			tributo			w 
		where	w.cd_tributo		= x.cd_tributo 
		and	x.nr_sequencia		= a.nr_sequencia 
		and	x.nr_item_nf		= b.nr_item_nf 
		and 	w.ie_tipo_tributo	= 'COFINS' 
		and	coalesce(x.ie_rateio,'N')	= 'N') vl_base_cofins, 
		--substr(replace(campo_mascara(a.vl_mercadoria,2),'.',','),1,15) vl_base_cofins, 
		(select	max(x.tx_tributo) 
		from	nota_fiscal_item_trib	x, 
			tributo			w 
		where	w.cd_tributo		= x.cd_tributo 
		and	x.nr_sequencia		= a.nr_sequencia 
		and	x.nr_item_nf		= b.nr_item_nf 
		and 	w.ie_tipo_tributo	= 'COFINS' 
		and	coalesce(x.ie_rateio,'N')	= 'N') pr_aliquota_cofins, 
		--substr(replace(campo_mascara(obter_pr_imposto(cd_tributo_cofins_w),2),'.',','),1,15) pr_aliquota_cofins, 
		null qt_base_cofins, 
		null vl_aliquota_cofins, 
		(select	coalesce(sum(x.vl_tributo),0) 
		from	nota_fiscal_item_trib	x, 
			tributo			w 
		where	w.cd_tributo		= x.cd_tributo 
		and	x.nr_sequencia		= a.nr_sequencia 
		and	x.nr_item_nf		= b.nr_item_nf 
		and 	w.ie_tipo_tributo	= 'COFINS' 
		and	coalesce(x.ie_rateio,'N')	= 'N') vl_cofins, 
		--substr(replace(campo_mascara((obter_vl_tributo_item(b.nr_sequencia, b.nr_item_nf, cd_tributo_cofins_w, 'VT')),2),'.',','),1,15) vl_cofins,	 
		b.cd_conta_contabil 
	FROM material m, nota_fiscal a, nota_fiscal_item b
LEFT OUTER JOIN material_fiscal c ON (b.cd_material = c.cd_material)
WHERE a.nr_sequencia		= b.nr_sequencia  and b.cd_material		= m.cd_material and a.cd_estabelecimento	= cd_estabelecimento_p and a.nr_sequencia		= nr_seq_superior_p order by 
		b.nr_item_nf;

vetval	c_valores%rowtype;


BEGIN 
 
select	nr_seq_regra_efd 
into STRICT	nr_seq_regra_efd_w 
from	fis_efd_controle 
where	nr_sequencia	= nr_seq_controle_p;
 
select 	cd_tributo_pis, 
	cd_tributo_cofins 
into STRICT	cd_tributo_pis_w, 
	cd_tributo_cofins_w 
from	fis_regra_efd 
where	nr_sequencia	= nr_seq_regra_efd_w;
 
open c_valores;
loop 
fetch c_valores into	 
	vetval;
EXIT WHEN NOT FOUND; /* apply on c_valores */
	begin	 
	if (ie_tipo_nota_w = 'E') then 
		cd_st_pis_w	:= '70';
		cd_st_cofins_w	:= '70';
	else 
		cd_st_pis_w	:= '01';
		cd_st_cofins_w	:= '01';
	end if;
	 
	nr_item_seq_w := nr_item_seq_w + 1;
	ds_linha_w	:= substr(	sep_w || vetval.tp_registro							|| --01 
					sep_w || nr_item_seq_w								|| --02 
					sep_w || vetval.cd_material							|| --03 
					sep_w || ''									|| --04 
					sep_w || substr(sped_obter_campo_valor(vetval.qt_item_nf), 1, 30)		|| --05 
					sep_w || vetval.cd_unidade_medida_compra					|| --06 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_total_item_nf), 1, 30)		|| --07 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_desconto), 1, 30)		|| --08 
					sep_w || vetval.ie_movimento							|| --09 
					sep_w || vetval.cd_st_icms							|| --10 
					sep_w || vetval.cd_cfop								|| --11 
					sep_w || vetval.cd_natureza_operacao						|| --12 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_base_icms), 1, 30)		|| --13 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_aliquota_icms), 1, 30)		|| --14 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_icms_deb_cred), 1, 30)		|| --15 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_base_icms_st), 1, 30)		|| --16 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_aliquota_st), 1, 30)		|| --17 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_icms_st), 1, 30)		|| --18 
					sep_w || vetval.ie_indicador_apuracao						|| --19 
					sep_w || '04'									|| --20 
					sep_w || vetval.cd_enquadramento_ipi						|| --21 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_base_ipi), 1, 30)		|| --22 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_aliquota_ipi), 1, 30)		|| --23 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_deb_cred_ipi), 1, 30)		|| --24 
					sep_w || vetval.cd_st_pis							|| --25 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_base_pis), 1, 30)		|| --26 
					sep_w || substr(sped_obter_campo_valor(vetval.pr_aliquota_pis), 1, 30)		|| --27 
					sep_w || substr(sped_obter_campo_valor(vetval.qt_base_pis), 1, 30)		|| --28 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_aliquota_pis), 1, 30)		|| --29 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_pis), 1, 30)			|| --30 
					sep_w || vetval.cd_st_cofins							|| --31 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_base_cofins), 1, 30)		|| --32 
					sep_w || substr(sped_obter_campo_valor(vetval.pr_aliquota_cofins), 1, 30)	|| --33 
					sep_w || substr(sped_obter_campo_valor(vetval.qt_base_cofins), 1, 30)		|| --34 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_aliquota_cofins), 1, 30)	|| --35 
					sep_w || substr(sped_obter_campo_valor(vetval.vl_cofins), 1, 30)		|| --36 
					sep_w || vetval.cd_conta_contabil						|| --37 
					sep_w,1,8000);
	 
	ds_arquivo_w		:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w	:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w	:= nr_seq_registro_w + 1;
	nr_linha_w		:= nr_linha_w + 1;
 
	insert into fis_efd_arquivo(nr_sequencia, 
		nm_usuario, 
		dt_atualizacao, 
		nm_usuario_nrec, 
		dt_atualizacao_nrec, 
		nr_seq_controle_efd, 
		nr_linha, 
		cd_registro, 
		ds_arquivo, 
		ds_arquivo_compl) 
	values (nr_seq_registro_w, 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nr_seq_controle_p, 
		nr_linha_w, 
		'C170', 
		ds_arquivo_w, 
		ds_arquivo_compl_w);
	end;
end loop;
close c_valores;
 
commit;
 
qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fis_gerar_reg_c170_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, nr_seq_superior_p bigint, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) FROM PUBLIC;

