SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE fis_gerar_reg_e100_icmsipi (nr_seq_controle_p bigint) AS $body$
DECLARE


/*REGISTRO E100: PERÍODO DA APURAÇÃO DO ICMS.*/

-- VARIABLES
ie_gerou_dados_bloco_w varchar(1) := 'N';

nr_seq_icmsipi_E100_w fis_efd_icmsipi_E100.nr_sequencia%type;

qt_cursor_w bigint := 0;
nr_vetor_w  bigint := 0;

-- USUARIO
nm_usuario_w usuario.nm_usuario%type;

/*Cursor que retorna as informações para o registro E100 restringindo pela sequencia da nota fiscal*/
c_reg_E100 CURSOR FOR
	SELECT	trunc(a.dt_inicio_apuracao) dt_ini,
		trunc(a.dt_fim_apuracao) dt_fin
	from	fis_efd_icmsipi_controle 	a,
		fis_efd_icmsipi_lote		b
	where	a.nr_seq_lote	= b.nr_sequencia
	and 	a.nr_sequencia 	= nr_seq_controle_p;

/*Criação do array com o tipo sendo do cursor eespecificado - c_reg_E100*/

type reg_c_reg_E100 is table of c_reg_E100%RowType;
vetRegE100 reg_c_reg_E100;

/*Criação do array com o tipo sendo da tabela eespecificada - FIS_EFD_ICMSIPI_E100 */

type registro is table of fis_efd_icmsipi_E100%rowtype index by integer;
fis_registros_w registro;

BEGIN
/*Obteção do usuário ativo no tasy*/

nm_usuario_w := Obter_Usuario_Ativo;

open c_reg_E100;
loop
fetch c_reg_E100 bulk collect into vetRegE100 limit 1000;
	for i in 1 .. vetRegE100.Count loop
	begin

	/*Incrementa a variavel para o array*/

	qt_cursor_w:=	qt_cursor_w + 1;

	if (ie_gerou_dados_bloco_w = 'N') then
	  ie_gerou_dados_bloco_w := 'S';
	end if;

	/*Busca da sequencia da tabela especificada - fis_efd_icmsipi_E100 */

	select nextval('fis_efd_icmsipi_e100_seq')	  into STRICT nr_seq_icmsipi_E100_w	;

	/*Inserindo valores no array para realização do forall posteriormente*/
	fis_registros_w[qt_cursor_w].nr_sequencia 		:= nr_seq_icmsipi_E100_w;
	fis_registros_w[qt_cursor_w].dt_atualizacao 		:= clock_timestamp();
	fis_registros_w[qt_cursor_w].nm_usuario 		:= nm_usuario_w;
	fis_registros_w[qt_cursor_w].dt_atualizacao_nrec 	:= clock_timestamp();
	fis_registros_w[qt_cursor_w].nm_usuario_nrec 		:= nm_usuario_w;
	fis_registros_w[qt_cursor_w].cd_reg 			:= 'E100';
	fis_registros_w[qt_cursor_w].dt_ini 			:= vetRegE100[i].dt_ini;	
	fis_registros_w[qt_cursor_w].dt_fin 			:= vetRegE100[i].dt_fin;	
	fis_registros_w[qt_cursor_w].nr_seq_controle 		:= nr_seq_controle_p;

	if (nr_vetor_w >= 1000) then
	begin
	/*Inserindo registros definitivamente na tabela especifica - FIS_EFD_ICMSIPI_E100 */

	forall i in fis_registros_w.first .. fis_registros_w.last
		insert into fis_efd_icmsipi_E100 values fis_registros_w(i);

		nr_vetor_w := 0;
		fis_registros_w.delete;

		commit;

	end;
	end if;

	/*incrementa variavel para realizar o forall quando chegar no valor limite*/

	nr_vetor_w := nr_vetor_w + 1;

	end;
	end loop;
EXIT WHEN NOT FOUND; /* apply on c_reg_E100 */
end loop;
close c_reg_E100;

if (fis_registros_w.count > 0) then
begin
/*Inserindo registro que não entraram outro for all devido a quantidade de registros no vetor*/

forall i in fis_registros_w.first .. fis_registros_w.last
	insert into fis_efd_icmsipi_E100 values fis_registros_w(i);

	fis_registros_w.delete;

	commit;

end;
end if;

/*Libera memoria*/

dbms_session.free_unused_user_memory;

/*Atualização informação no controle de geração de registro para SIM*/

if (ie_gerou_dados_bloco_w = 'S') then
	update fis_efd_icmsipi_controle
	set ie_mov_E = 'S'
	where nr_sequencia = nr_seq_controle_p;
end if;

/*Chama E110 - FIlho*/

CALL fis_gerar_reg_E110_icmsipi(nr_seq_controle_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE fis_gerar_reg_e100_icmsipi (nr_seq_controle_p bigint) FROM PUBLIC;

