CREATE OR REPLACE PROCEDURE fis_gerar_reg_e111_efd ( nr_seq_controle_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, dt_inicio_p timestamp, dt_fim_p timestamp, cd_empresa_p bigint, ds_separador_p text, qt_linha_p INOUT bigint, nr_sequencia_p INOUT bigint) AS $body$
DECLARE


nr_linha_w			bigint := qt_linha_p;
nr_seq_registro_w		bigint := nr_sequencia_p;
ds_arquivo_w			varchar(4000);
ds_arquivo_compl_w		varchar(4000);
ds_linha_w			varchar(8000);
sep_w				varchar(1)	:= ds_separador_p;
dt_fim_w			timestamp;

c01 CURSOR FOR
SELECT	distinct 'E111' tp_registro,
	'SC039999' cod_aj_apur,
	'' descr_compl_aj,
	sum(nfe_obter_valores_totais_num(a.nr_sequencia, 'ICMS', 'VL')) vl_aj_apur
from	nota_fiscal a
where	a.cd_estabelecimento	= cd_estabelecimento_p
--and	a.cd_operacao_nf in (30, 44, 4, 31, 25, 35, 23, 1, 2, 29, 42, 41, 21, 28)
and 	Obter_se_nota_entrada_saida(a.nr_sequencia) = 'S'
and     a.dt_emissao between dt_inicio_p and dt_fim_w  --nota de saida
and 	a.ie_situacao = 1
and	a.ie_tipo_nota in ('SD', 'SE');

vet01	c01%rowtype;


BEGIN

dt_fim_w := fim_dia(dt_fim_p); --coloca a hora junto ex: 01/01/2013 23:59:59
open c01;
loop
fetch c01 into
	vet01;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_linha_w	:= substr(	sep_w || vet01.tp_registro	||
					sep_w || vet01.cod_aj_apur			||
					sep_w || vet01.descr_compl_aj				||
					sep_w || vet01.vl_aj_apur		|| sep_w,1,8000);

	ds_arquivo_w			:= substr(ds_linha_w,1,4000);
	ds_arquivo_compl_w		:= substr(ds_linha_w,4001,4000);
	nr_seq_registro_w		:= nr_seq_registro_w + 1;
	nr_linha_w			:= nr_linha_w + 1;

	insert into fis_efd_arquivo(
		nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_controle_efd,
		nr_linha,
		cd_registro,
		ds_arquivo,
		ds_arquivo_compl)
	values (	nr_seq_registro_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_controle_p,
		nr_linha_w,
		'E111',
		ds_arquivo_w,
		ds_arquivo_compl_w);
	end;
end loop;
close c01;

commit;

qt_linha_p	:= nr_linha_w;
nr_sequencia_p	:= nr_seq_registro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
